import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/token/', {
        username,
        password,
      });
      localStorage.setItem('access_token', response.data.access);
      localStorage.setItem('refresh_token', response.data.refresh);
      navigate('/');
    } catch (err) {
      setError('Credenciais inválidas. Tente novamente.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900 text-white">
      <div className="w-full max-w-lg bg-gray-800 rounded-lg shadow-lg p-8">
        {/* Cabeçalho */}
        <div className="flex flex-col items-center mb-6">
          <img
            src="https://www.levesaude.com.br/build/assets/logo-menu-leve-5752d94b.png"
            alt="Leve Saúde"
            className="h-12 mb-2"
          />
          <h1 className="text-2xl font-bold mb-1">Fazer login</h1>
          <p className="text-sm text-gray-400">Ir para Leve Saúde</p>
        </div>

        {/* Formulário */}
        <form onSubmit={handleLogin} className="space-y-6">
          {/* Campo Usuário */}
          <div>
            <label htmlFor="username" className="block text-sm font-medium mb-2">
              E-mail ou telefone
            </label>
            <input
              id="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full p-3 rounded bg-gray-700 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Digite seu e-mail ou telefone"
              required
            />
          </div>

          {/* Campo Senha */}
          <div>
            <label htmlFor="password" className="block text-sm font-medium mb-2">
              Senha
            </label>
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-3 rounded bg-gray-700 border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Digite sua senha"
              required
            />
          </div>

          {/* Mensagem de erro */}
          {error && (
            <p className="text-red-500 text-sm text-center">
              {error}
            </p>
          )}

          {/* Botão de Login */}
          <button
            type="submit"
            className="w-full py-3 bg-blue-500 text-white font-bold rounded hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-300"
          >
            Próxima
          </button>
        </form>

        {/* Links adicionais */}
        <div className="mt-6 text-sm text-center">
          <a
            href="/esqueceu-senha"
            className="text-blue-400 hover:underline"
          >
            Esqueceu seu e-mail?
          </a>
          <p className="mt-4 text-gray-400">
            Não está no seu computador? Use o modo visitante para fazer login com privacidade.{' '}
            <a href="/modo-visitante" className="text-blue-400 hover:underline">
              Saiba mais
            </a>
          </p>
        </div>

        {/* Rodapé */}
        <div className="mt-6 text-sm text-center text-gray-500">
          <a href="/ajuda" className="mr-4 hover:underline">
            Ajuda
          </a>
          <a href="/privacidade" className="mr-4 hover:underline">
            Privacidade
          </a>
          <a href="/termos" className="hover:underline">
            Termos
          </a>
        </div>
      </div>
    </div>
  );
}

export default Login;
