import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/token/', {
        username,
        password,
      });
      localStorage.setItem('access_token', response.data.access);
      localStorage.setItem('refresh_token', response.data.refresh);
      navigate('/');
    } catch (err) {
      setError('Credenciais inválidas. Tente novamente.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-700 via-purple-800 to-purple-900">
      <div className="w-full max-w-sm bg-white shadow-xl rounded-lg p-8">
        <div className="flex justify-center mb-6">
          <img
            src="https://www.levesaude.com.br/build/assets/logo-menu-leve-5752d94b.png"
            alt="Leve Saúde"
            className="h-16 transform hover:scale-110 transition-transform duration-300"
          />
        </div>

        <h1 className="text-2xl font-bold text-center text-gray-800 mb-4">
          Acesse a sua Conta
        </h1>

        <form onSubmit={handleLogin} className="space-y-4">
          {/* Campo Usuário */}
          <div className="flex flex-col">
            <input
              id="username"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              className="w-full p-3 rounded bg-gray-100 border border-gray-300 focus:ring-2 focus:ring-purple-500 focus:outline-none"
              placeholder="Digite seu usuário"
              required
            />
          </div>

          {/* Campo Senha */}
          <div className="flex flex-col">
            <input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-3 rounded bg-gray-100 border border-gray-300 focus:ring-2 focus:ring-purple-500 focus:outline-none"
              placeholder="Digite sua senha"
              required
            />
          </div>

          {/* Mensagem de erro */}
          {error && (
            <div className="text-red-500 text-sm text-center">
              {error}
            </div>
          )}

          {/* Botão de Login */}
          <button
            type="submit"
            className="w-full p-3 rounded bg-purple-600 text-white font-bold hover:bg-purple-700 transition-all focus:ring-2 focus:ring-purple-300"
          >
            Entrar
          </button>
        </form>
      </div>
    </div>
  );
}

export default Login;
