import React, { useState } from "react";
import api from "../services/api";

const AtivoFormModal = () => {
    const [selectedAtivo, setSelectedAtivo] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState({
        nome: "",
        codigo_patrimonio: "",
        categoria: "Notebook",
        localidade: "",
        setor: "",
        responsavel: "",
        status: "Ativo",
        data_ultima_avaliacao: "",
        data_proxima_avaliacao: "",
        modelo: "",
        marca: "",
        numero_serie: "",
        mac_address_ethernet: "",
        mac_address_wifi: "",
        ip_fixado: "",
        bitlocker: false,
        antivirus_licenca: "",
        antivirus_versao: "",
        bart_wazuh: false,
        chrome_enterprise: false,
        acesso_remoto_id: "",
        termo_custodia: false,
    });

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData({
            ...formData,
            [name]: type === "checkbox" ? checked : value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (isEditing) {
            // Lógica de atualização
            api.put(`ativos/${selectedAtivo.id}/`, formData)
                .then(() => {
                    alert("Ativo atualizado com sucesso!");
                    // Resetar formulário e estado
                })
                .catch(() => alert("Erro ao atualizar ativo."));
        } else {
            // Lógica de criação
            api.post("ativos/", formData)
                .then(() => {
                    alert("Ativo adicionado com sucesso!");
                    // Resetar formulário
                })
                .catch(() => alert("Erro ao adicionar ativo."));
        }
    };

    const handleEdit = (ativo) => {
        setSelectedAtivo(ativo);
        setFormData(ativo);
        setIsEditing(true);
    };

    const handleView = (ativo) => {
        setSelectedAtivo(ativo);
    };

    return (
        <div>
            {/* Modal de Visualização de Ativo */}
            <div 
                className="modal fade" 
                id="viewAtivoModal" 
                tabIndex="-1" 
                aria-hidden="true"
            >
                <div className="modal-dialog modal-lg">
                    <div className="modal-content">
                        <div className="modal-header bg-info text-white">
                            <h5 className="modal-title">Detalhes do Ativo</h5>
                            <button 
                                type="button" 
                                className="btn-close" 
                                data-bs-dismiss="modal"
                                aria-label="Close"
                            ></button>
                        </div>
                        <div className="modal-body">
                            {selectedAtivo && (
                                <div className="row">
                                    <div className="col-md-6">
                                        <h6 className="text-muted">Informações Básicas</h6>
                                        <p><strong>Nome:</strong> {selectedAtivo.nome}</p>
                                        <p><strong>Código Patrimônio:</strong> {selectedAtivo.codigo_patrimonio}</p>
                                        <p><strong>Categoria:</strong> {selectedAtivo.categoria}</p>
                                        <p><strong>Status:</strong> {selectedAtivo.status}</p>
                                    </div>
                                    <div className="col-md-6">
                                        <h6 className="text-muted">Detalhes de Localização</h6>
                                        <p><strong>Localidade:</strong> {selectedAtivo.localidade}</p>
                                        <p><strong>Setor:</strong> {selectedAtivo.setor}</p>
                                        <p><strong>Responsável:</strong> {selectedAtivo.responsavel}</p>
                                    </div>
                                    {/* Adicionar mais seções conforme necessário */}
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>

            {/* Modal de Edição com Design Aprimorado */}
            <div 
                className="modal fade" 
                id="ativoFormModal" 
                tabIndex="-1" 
                aria-hidden="true"
            >
                <div className="modal-dialog modal-lg">
                    <div className="modal-content">
                        <div 
                            className="modal-header" 
                            style={{ 
                                backgroundColor: isEditing ? '#28a745' : '#451e44', 
                                color: 'white' 
                            }}
                        >
                            <h5 className="modal-title">
                                {isEditing ? 'Editar Ativo' : 'Adicionar Novo Ativo'}
                            </h5>
                            <button 
                                type="button" 
                                className="btn-close btn-close-white" 
                                data-bs-dismiss="modal" 
                                aria-label="Close"
                            ></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={handleSubmit}>
                                {/* Design do formulário permanece o mesmo, mas com melhorias visuais */}
                                <div className="row">
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">Nome do Ativo</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="nome"
                                            value={formData.nome}
                                            onChange={handleChange}
                                            required
                                            placeholder="Digite o nome do ativo"
                                        />
                                    </div>
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">Código de Patrimônio</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="codigo_patrimonio"
                                            value={formData.codigo_patrimonio}
                                            onChange={handleChange}
                                            required
                                            placeholder="Código único de patrimônio"
                                        />
                                    </div>
                                </div>

                                {/* Categoria agora inclui as novas opções */}
                                <div className="row">
                                    <div className="col-md-4 mb-3">
                                        <label className="form-label">Categoria</label>
                                        <select
                                            className="form-select"
                                            name="categoria"
                                            value={formData.categoria}
                                            onChange={handleChange}
                                        >
                                            <option value="Notebook">Notebook</option>
                                            <option value="Desktop">Desktop</option>
                                            <option value="Smartphone">Smartphone</option>
                                            <option value="Tablet">Tablet</option>
                                            <option value="Monitor">Monitor</option>
                                        </select>
                                    </div>
                                    {/* Restante do formulário permanece igual */}
                                </div>

                                {/* Botões com design aprimorado */}
                                <div className="d-flex justify-content-end mt-3">
                                    <button 
                                        type="submit" 
                                        className={`btn ${isEditing ? 'btn-success' : 'btn-primary'} me-2`}
                                    >
                                        {isEditing ? 'Atualizar' : 'Salvar'}
                                    </button>
                                    <button 
                                        type="button" 
                                        className="btn btn-secondary" 
                                        data-bs-dismiss="modal"
                                    >
                                        Cancelar
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AtivoFormModal;
