import React, { useEffect, useState } from "react";
import api from "../api";

const AtivoList = () => {
    const [ativos, setAtivos] = useState([]);

    useEffect(() => {
        api.get("ativos/")
            .then((response) => {
                setAtivos(response.data);
            })
            .catch((error) => {
                console.error("Erro ao buscar ativos:", error);
            });
    }, []);

    return (
        <div className="container mt-4">
            <h2>Lista de Ativos</h2>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Nome</th>
                        <th>Patrimônio</th>
                        <th>Categoria</th>
                        <th>Responsável</th>
                        <th>Última Avaliação</th>
                        <th>Próxima Avaliação</th>
                    </tr>
                </thead>
                <tbody>
                    {ativos.map((ativo) => (
                        <tr key={ativo.id}>
                            <td>{ativo.nome}</td>
                            <td>{ativo.codigo_patrimonio}</td>
                            <td>{ativo.categoria}</td>
                            <td>{ativo.responsavel}</td>
                            <td>{ativo.data_ultima_avaliacao}</td>
                            <td>{ativo.data_proxima_avaliacao}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AtivoList;
