import React, { useEffect, useState, useCallback } from "react";
import api from "../services/api";

const AtivoList = () => {
  const [ativos, setAtivos] = useState([]);
  const [filteredAtivos, setFilteredAtivos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("todos");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "ascending" });
  const [viewingAtivo, setViewingAtivo] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editingAtivo, setEditingAtivo] = useState(null);
  const [deletedAtivo, setDeletedAtivo] = useState(null);

  const loadAtivos = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get("ativos/");
      setAtivos(response.data);
      setFilteredAtivos(response.data);
    } catch (error) {
      console.error("Erro ao buscar ativos:", error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadAtivos();
  }, [loadAtivos]);

  const handleViewDetails = (ativo) => {
    setViewingAtivo(ativo);
  };

  const handleSaveEdit = async () => {
    try {
      await api.put(`ativos/${editingAtivo.id}/`, editingAtivo);
      setEditMode(false);
      setEditingAtivo(null);
      loadAtivos();
    } catch (error) {
      console.error("Erro ao editar ativo:", error);
    }
  };

  const handleDelete = async (ativo) => {
    try {
      await api.delete(`ativos/${ativo.id}/`);
      setDeletedAtivo(ativo);
      setAtivos(ativos.filter((a) => a.id !== ativo.id));
    } catch (error) {
      console.error("Erro ao excluir ativo:", error);
    }
  };

  const handleUndoDelete = async () => {
    try {
      await api.post("ativos/", deletedAtivo);
      setDeletedAtivo(null);
      loadAtivos();
    } catch (error) {
      console.error("Erro ao desfazer exclusÃ£o:", error);
    }
  };

  return (
    <div className="container mt-4">
      <div className="card shadow-sm">
        <div className="card-header bg-primary text-white py-3 d-flex justify-content-between align-items-center">
          <h2 className="h4 fw-bold mb-0">Lista de Ativos</h2>
          <div className="d-flex gap-3">
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="todos">Todas as Categorias</option>
              <option value="Notebook">Notebook</option>
              <option value="Desktop">Desktop</option>
            </select>
            <div className="input-group">
              <span className="input-group-text bg-light border-end-0">
                <i className="bi bi-search"></i>
              </span>
              <input
                type="text"
                className="form-control border-start-0 ps-0"
                placeholder="Pesquisar..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
        </div>
        <div className="card-body p-0">
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Carregando...</span>
              </div>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover mb-0 align-middle">
                <thead>
                  <tr className="bg-light">
                    <th>Nome</th>
                    <th>PatrimÃ´nio</th>
                    <th>Categoria</th>
                    <th>ResponsÃ¡vel</th>
                    <th>AÃ§Ãµes</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredAtivos.map((ativo) => (
                    <tr key={ativo.id}>
                      <td>{ativo.nome}</td>
                      <td>{ativo.codigo_patrimonio}</td>
                      <td>{ativo.categoria}</td>
                      <td>{ativo.responsavel}</td>
                      <td className="d-flex gap-2">
                        <button
                          className="btn btn-sm btn-info"
                          onClick={() => handleViewDetails(ativo)}
                        >
                          Visualizar Detalhes
                        </button>
                        <button
                          className="btn btn-sm btn-warning"
                          onClick={() => {
                            setEditMode(true);
                            setEditingAtivo(ativo);
                          }}
                        >
                          Editar
                        </button>
                        <button
                          className="btn btn-sm btn-danger"
                          onClick={() => handleDelete(ativo)}
                        >
                          Excluir
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Modal para exibir detalhes do ativo */}
      {viewingAtivo && (
        <div className="modal fade show d-block" style={{ background: "rgba(0, 0, 0, 0.5)" }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header bg-primary text-white">
                <h5 className="modal-title">Detalhes do Ativo</h5>
                <button
                  className="btn-close btn-close-white"
                  onClick={() => setViewingAtivo(null)}
                ></button>
              </div>
              <div className="modal-body">
                <table className="table table-striped">
                  <tbody>
                    {Object.entries(viewingAtivo).map(([key, value]) => (
                      <tr key={key}>
                        <td className="fw-bold text-capitalize">
                          {key.replace(/_/g, " ")}:
                        </td>
                        <td>{value || "-"}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setViewingAtivo(null)}
                >
                  Fechar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modal para editar ativo */}
      {editMode && editingAtivo && (
        <div className="modal fade show d-block" style={{ background: "rgba(0, 0, 0, 0.5)" }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Editar Ativo</h5>
                <button
                  className="btn-close"
                  onClick={() => setEditMode(false)}
                ></button>
              </div>
              <div className="modal-body">
                {Object.keys(editingAtivo).map((key) => (
                  <div className="mb-3" key={key}>
                    <label className="form-label text-capitalize">
                      {key.replace(/_/g, " ")}
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      value={editingAtivo[key] || ""}
                      onChange={(e) =>
                        setEditingAtivo({ ...editingAtivo, [key]: e.target.value })
                      }
                    />
                  </div>
                ))}
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setEditMode(false)}
                >
                  Cancelar
                </button>
                <button
                  className="btn btn-success"
                  onClick={handleSaveEdit}
                >
                  Salvar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {deletedAtivo && (
        <div className="alert alert-warning d-flex justify-content-between align-items-center mt-3">
          <span>Ativo excluÃ­do. Deseja desfazer?</span>
          <button className="btn btn-sm btn-primary" onClick={handleUndoDelete}>
            Desfazer
          </button>
        </div>
      )}
    </div>
  );
};

export default AtivoList;
