import React, { useEffect, useState, useCallback, useMemo } from "react";
import api from "../services/api";

const CATEGORIES = [
  { value: "todos", label: "Todas as Categorias" },
  { value: "Notebook", label: "Notebook" },
  { value: "Desktop", label: "Desktop" },
  { value: "Smartphone", label: "Smartphone" },
  { value: "Monitor", label: "Monitor" }
];

const AtivoList = () => {
  const [ativos, setAtivos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("todos");
  const [selectedAtivo, setSelectedAtivo] = useState(null);

  // Função para carregar os ativos
  const loadAtivos = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get("ativos/");
      setAtivos(response.data);
    } catch (error) {
      console.error("Erro ao buscar ativos:", error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadAtivos();
  }, [loadAtivos]);

  // Filtragem de ativos com base na pesquisa e categoria
  const filteredAtivos = useMemo(() => {
    return ativos.filter((ativo) => {
      const searchFields = [
        ativo.nome, 
        ativo.codigo_patrimonio, 
        ativo.categoria, 
        ativo.responsavel
      ];

      const matchesSearch = searchFields.some(field => 
        field?.toLowerCase().includes(searchTerm.toLowerCase())
      );

      const matchesCategory = selectedCategory === "todos" || 
        ativo.categoria === selectedCategory;

      return matchesSearch && matchesCategory;
    });
  }, [searchTerm, selectedCategory, ativos]);

  // Formatação de datas
  const formatDate = (dateString) =>
    !dateString ? "-" : new Date(dateString).toLocaleDateString("pt-BR");

  // Renderização condicional do conteúdo da tabela
  const renderTableContent = () => {
    if (loading) {
      return (
        <tr>
          <td colSpan="8" className="text-center py-5">
            <div className="spinner-border text-primary" role="status">
              <span className="visually-hidden">Carregando...</span>
            </div>
          </td>
        </tr>
      );
    }

    if (filteredAtivos.length === 0) {
      return (
        <tr>
          <td colSpan="8" className="text-center py-4 text-muted">
            Nenhum ativo encontrado
          </td>
        </tr>
      );
    }

    return filteredAtivos.map((ativo) => (
      <tr key={ativo.id}>
        <td>{ativo.nome}</td>
        <td>{ativo.codigo_patrimonio}</td>
        <td>
          <span className="badge bg-secondary text-white">
            {ativo.categoria}
          </span>
        </td>
        <td>{ativo.responsavel}</td>
        <td>{ativo.setor}</td>
        <td>{formatDate(ativo.data_ultima_avaliacao)}</td>
        <td>{formatDate(ativo.data_proxima_avaliacao)}</td>
        <td>
          <button
            className="btn btn-sm btn-info me-2"
            onClick={() => setSelectedAtivo(ativo)}
            data-bs-toggle="modal"
            data-bs-target="#visualizarModal"
          >
            Visualizar
          </button>
          <button className="btn btn-sm btn-warning me-2">
            Editar
          </button>
          <button className="btn btn-sm btn-danger">
            Excluir
          </button>
        </td>
      </tr>
    ));
  };

  return (
    <div className="container mt-4">
      <div className="card shadow-sm">
        {/* Cabeçalho da Tabela */}
        <div className="card-header bg-white py-3 d-flex justify-content-between align-items-center">
          <h2 className="h4 fw-bold mb-0">Lista de Ativos</h2>
          <div className="d-flex gap-3">
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              {CATEGORIES.map(category => (
                <option key={category.value} value={category.value}>
                  {category.label}
                </option>
              ))}
            </select>
            <div className="input-group">
              <span className="input-group-text bg-light border-end-0">
                <i className="bi bi-search"></i>
              </span>
              <input
                type="text"
                className="form-control border-start-0 ps-0"
                placeholder="Pesquisar..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
        </div>

        {/* Corpo da Tabela */}
        <div className="card-body p-0">
          <div className="table-responsive">
            <table className="table table-hover mb-0 align-middle">
              <thead>
                <tr className="bg-light">
                  <th>Nome do Ativo</th>
                  <th>Patrimônio</th>
                  <th>Categoria</th>
                  <th>Responsável</th>
                  <th>Setor</th>
                  <th>Última Avaliação</th>
                  <th>Próxima Avaliação</th>
                  <th>Ações</th>
                </tr>
              </thead>
              <tbody>
                {renderTableContent()}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* Modal de Visualização */}
      <div
        className="modal fade"
        id="visualizarModal"
        tabIndex="-1"
        aria-labelledby="visualizarModalLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog modal-lg">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="visualizarModalLabel">
                Informações do Ativo
              </h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div className="modal-body">
              {selectedAtivo ? (
                <div className="row">
                  {Object.entries(selectedAtivo).map(([key, value]) => (
                    <div key={key} className="col-md-6 mb-3">
                      <strong>{key.replace(/_/g, " ").toUpperCase()}:</strong>
                      <p className="text-muted mb-0">
                        {value || "Não informado"}
                      </p>
                    </div>
                  ))}
                </div>
              ) : (
                <p>Nenhum ativo selecionado</p>
              )}
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                data-bs-dismiss="modal"
              >
                Fechar
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AtivoList;
