import React, { useEffect, useState, useCallback } from "react";
import api from "../services/api";

const AtivoList = () => {
  const [ativos, setAtivos] = useState([]);
  const [filteredAtivos, setFilteredAtivos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("todos");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "ascending" });
  const [editingAtivo, setEditingAtivo] = useState(null);
  const [selectedAtivo, setSelectedAtivo] = useState(null); // Ativo selecionado para detalhes
  const [deletedAtivo, setDeletedAtivo] = useState(null); // Ativo excluído temporariamente

  // Carregar ativos
  const loadAtivos = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get("ativos/");
      setAtivos(response.data);
      setFilteredAtivos(response.data);
    } catch (error) {
      console.error("Erro ao buscar ativos:", error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadAtivos();
  }, [loadAtivos]);

  // Filtrar ativos com base em pesquisa e categoria
  useEffect(() => {
    const filtered = ativos.filter((ativo) => {
      const matchesSearch =
        ativo.nome?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        ativo.codigo_patrimonio?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        ativo.categoria?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        ativo.responsavel?.toLowerCase().includes(searchTerm.toLowerCase());

      const matchesCategory = selectedCategory === "todos" || ativo.categoria === selectedCategory;

      return matchesSearch && matchesCategory;
    });

    setFilteredAtivos(filtered);
  }, [searchTerm, selectedCategory, ativos]);

  // Ordenar ativos
  const handleSort = (key) => {
    const direction = sortConfig.key === key && sortConfig.direction === "ascending" ? "descending" : "ascending";
    setSortConfig({ key, direction });

    const sortedAtivos = [...filteredAtivos].sort((a, b) => {
      if (a[key] < b[key]) return direction === "ascending" ? -1 : 1;
      if (a[key] > b[key]) return direction === "ascending" ? 1 : -1;
      return 0;
    });

    setFilteredAtivos(sortedAtivos);
  };

  // Salvar edição do ativo
  const handleSaveEdit = async () => {
    try {
      await api.put(`ativos/${editingAtivo.id}/`, editingAtivo);
      setEditingAtivo(null);
      loadAtivos();
    } catch (error) {
      console.error("Erro ao salvar edição:", error);
    }
  };

  // Excluir ativo
  const handleDelete = async (ativo) => {
    try {
      await api.delete(`ativos/${ativo.id}/`);
      setDeletedAtivo(ativo); // Salva o ativo para possível restauração
      setAtivos(ativos.filter((a) => a.id !== ativo.id));
    } catch (error) {
      console.error("Erro ao excluir ativo:", error);
    }
  };

  // Restaurar ativo excluído
  const handleUndoDelete = async () => {
    try {
      await api.post("ativos/", deletedAtivo);
      setDeletedAtivo(null);
      loadAtivos();
    } catch (error) {
      console.error("Erro ao restaurar ativo:", error);
    }
  };

  const formatDate = (date) => (!date ? "-" : new Date(date).toLocaleDateString("pt-BR"));

  return (
    <div className="container mt-4">
      <div className="card shadow-sm">
        <div className="card-header bg-white py-3 d-flex justify-content-between align-items-center">
          <h2 className="h4 fw-bold mb-0">Lista de Ativos</h2>
          <div className="d-flex gap-3">
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="todos">Todas as Categorias</option>
              <option value="Notebook">Notebook</option>
              <option value="Desktop">Desktop</option>
            </select>
            <div className="input-group">
              <span className="input-group-text bg-light border-end-0">
                <i className="bi bi-search"></i>
              </span>
              <input
                type="text"
                className="form-control border-start-0 ps-0"
                placeholder="Pesquisar..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={{ outline: "none", boxShadow: "none" }}
              />
            </div>
          </div>
        </div>

        <div className="card-body p-0">
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Carregando...</span>
              </div>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover mb-0 align-middle">
                <thead>
                  <tr className="bg-light">
                    {[{ key: "nome", label: "Nome" },
                      { key: "codigo_patrimonio", label: "Patrimônio" },
                      { key: "categoria", label: "Categoria" },
                      { key: "responsavel", label: "Responsável" },
                      { key: "data_ultima_avaliacao", label: "Última Avaliação" },
                      { key: "data_proxima_avaliacao", label: "Próxima Avaliação" },
                      { key: "acoes", label: "Ações" },
                    ].map(({ key, label }) => (
                      <th key={key} className="px-4 py-3 text-dark fw-semibold" style={{ cursor: "pointer" }}>
                        {label}
                        {sortConfig.key === key && (sortConfig.direction === "ascending" ? " ↑" : " ↓")}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody>
                  {filteredAtivos.length === 0 ? (
                    <tr>
                      <td colSpan="7" className="text-center py-4 text-muted">Nenhum ativo encontrado</td>
                    </tr>
                  ) : (
                    filteredAtivos.map((ativo) => (
                      <tr
                        key={ativo.id}
                        onClick={() => setSelectedAtivo(ativo)}
                        style={{ cursor: "pointer" }}
                        data-bs-toggle="modal"
                        data-bs-target="#detailsModal"
                      >
                        <td className="px-4 py-3 fw-medium">{ativo.nome}</td>
                        <td className="px-4 py-3">{ativo.codigo_patrimonio}</td>
                        <td className="px-4 py-3"><span className="badge bg-light text-dark">{ativo.categoria}</span></td>
                        <td className="px-4 py-3">{ativo.responsavel}</td>
                        <td className="px-4 py-3 text-muted">{formatDate(ativo.data_ultima_avaliacao)}</td>
                        <td className="px-4 py-3 text-muted">{formatDate(ativo.data_proxima_avaliacao)}</td>
                        <td className="px-4 py-3">
                          <button className="btn btn-sm btn-warning me-2" onClick={() => setEditingAtivo(ativo)}>
                            Editar
                          </button>
                          <button className="btn btn-sm btn-danger" onClick={() => handleDelete(ativo)}>
                            Excluir
                          </button>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          )}
        </div>

        {/* Modal de Detalhes */}
        {selectedAtivo && (
          <div
            className="modal fade show"
            id="detailsModal"
            tabIndex="-1"
            aria-labelledby="detailsModalLabel"
            aria-hidden="true"
            style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}
          >
            <div className="modal-dialog modal-lg">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">Detalhes do Ativo</h5>
                  <button className="btn-close" onClick={() => setSelectedAtivo(null)}></button>
                </div>
                <div className="modal-body">
                  <ul className="list-group">
                    {Object.entries(selectedAtivo).map(([key, value]) => (
                      <li className="list-group-item d-flex justify-content-between" key={key}>
                        <span className="fw-bold text-capitalize">{key.replace(/_/g, " ")}:</span>
                        <span>{value || "-"}</span>
                      </li>
                    ))}
                  </ul>
                </div>
                <div className="modal-footer">
                  <button className="btn btn-secondary" onClick={() => setSelectedAtivo(null)}>
                    Fechar
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Modal de Edição */}
        {editingAtivo && (
          <div
            className="modal fade show"
            id="editModal"
            tabIndex="-1"
            aria-labelledby="editModalLabel"
            aria-hidden="true"
            style={{ display: "block", backgroundColor: "rgba(0,0,0,0.5)" }}
          >
            <div className="modal-dialog modal-lg">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">Editar Ativo</h5>
                  <button className="btn-close" onClick={() => setEditingAtivo(null)}></button>
                </div>
                <div className="modal-body">
                  {Object.keys(editingAtivo).map((key) => (
                    <div className="mb-3" key={key}>
                      <label className="form-label text-capitalize">{key.replace(/_/g, " ")}</label>
                      <input
                        type="text"
                        className="form-control"
                        value={editingAtivo[key] || ""}
                        onChange={(e) => setEditingAtivo({ ...editingAtivo, [key]: e.target.value })}
                      />
                    </div>
                  ))}
                </div>
                <div className="modal-footer">
                  <button className="btn btn-secondary" onClick={() => setEditingAtivo(null)}>
                    Cancelar
                  </button>
                  <button className="btn btn-success" onClick={handleSaveEdit}>
                    Salvar Alterações
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Alerta de Desfazer Exclusão */}
        {deletedAtivo && (
          <div className="alert alert-warning d-flex justify-content-between align-items-center mt-3">
            <span>Ativo excluído. Deseja desfazer?</span>
            <button className="btn btn-sm btn-primary" onClick={handleUndoDelete}>
              Desfazer
            </button>
          </div>
        )}

        <div className="card-footer bg-white py-3">
          <small className="text-muted">Total de ativos: {filteredAtivos.length}</small>
        </div>
      </div>
    </div>
  );
};

export default AtivoList;
