import React, { useEffect, useState, useCallback } from "react";
import api from "../services/api";

const AtivoList = () => {
  const [ativos, setAtivos] = useState([]);
  const [filteredAtivos, setFilteredAtivos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("todos");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "ascending" });
  const [viewingAtivo, setViewingAtivo] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editingAtivo, setEditingAtivo] = useState(null);

  const loadAtivos = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get("ativos/");
      setAtivos(response.data);
      setFilteredAtivos(response.data);
    } catch (error) {
      console.error("Erro ao buscar ativos:", error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadAtivos();
  }, [loadAtivos]);

  const handleSort = (key) => {
    const direction = sortConfig.key === key && sortConfig.direction === "ascending" ? "descending" : "ascending";
    setSortConfig({ key, direction });

    const sortedAtivos = [...filteredAtivos].sort((a, b) => {
      if (a[key] < b[key]) return direction === "ascending" ? -1 : 1;
      if (a[key] > b[key]) return direction === "ascending" ? 1 : -1;
      return 0;
    });

    setFilteredAtivos(sortedAtivos);
  };

  const handleSaveEdit = async () => {
    try {
      await api.put(`ativos/${editingAtivo.id}/`, editingAtivo);
      setEditMode(false);
      setEditingAtivo(null);
      loadAtivos();
    } catch (error) {
      console.error("Erro ao editar ativo:", error);
    }
  };

  const formatDate = (dateString) =>
    !dateString ? "-" : new Date(dateString).toLocaleDateString("pt-BR");

  return (
    <div className="container mt-4">
      <div className="card shadow-sm">
        <div className="card-header bg-light text-dark py-3 d-flex justify-content-between align-items-center">
          <h2 className="h4 fw-bold mb-0">Lista de Ativos</h2>
          <div className="d-flex gap-3">
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="todos">Todas as Categorias</option>
              <option value="Notebook">Notebook</option>
              <option value="Desktop">Desktop</option>
            </select>
            <div className="input-group">
              <span className="input-group-text bg-light border-end-0">
                <i className="bi bi-search"></i>
              </span>
              <input
                type="text"
                className="form-control border-start-0 ps-0"
                placeholder="Pesquisar..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
        </div>
        <div className="card-body p-0">
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Carregando...</span>
              </div>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover mb-0 align-middle">
                <thead>
                  <tr className="bg-light">
                    {["nome", "codigo_patrimonio", "categoria", "responsavel"].map((key) => (
                      <th
                        key={key}
                        onClick={() => handleSort(key)}
                        className="text-dark fw-semibold px-4 py-3"
                        style={{ cursor: "pointer" }}
                      >
                        {key.replace(/_/g, " ").toUpperCase()}
                        {sortConfig.key === key && (
                          <span>{sortConfig.direction === "ascending" ? " ↑" : " ↓"}</span>
                        )}
                      </th>
                    ))}
                    <th className="text-dark fw-semibold px-4 py-3">AÇÕES</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredAtivos.map((ativo) => (
                    <tr key={ativo.id}>
                      <td>{ativo.nome}</td>
                      <td>{ativo.codigo_patrimonio}</td>
                      <td>{ativo.categoria}</td>
                      <td>{ativo.responsavel}</td>
                      <td className="d-flex gap-2">
                        <button
                          className="btn btn-sm btn-info"
                          onClick={() => setViewingAtivo(ativo)}
                        >
                          Visualizar Detalhes
                        </button>
                        <button
                          className="btn btn-sm btn-warning"
                          onClick={() => {
                            setEditMode(true);
                            setEditingAtivo(ativo);
                          }}
                        >
                          Editar
                        </button>
                        <button className="btn btn-sm btn-danger">Excluir</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Modal de Visualizar Detalhes */}
      {viewingAtivo && (
        <div className="modal fade show d-block" style={{ background: "rgba(0, 0, 0, 0.5)" }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header bg-dark text-white">
                <h5 className="modal-title">Detalhes do Ativo</h5>
                <button
                  className="btn-close btn-close-white"
                  onClick={() => setViewingAtivo(null)}
                ></button>
              </div>
              <div className="modal-body">
                <table className="table table-striped">
                  <tbody>
                    {Object.entries(viewingAtivo).map(([key, value]) => (
                      <tr key={key}>
                        <td className="fw-bold text-capitalize">
                          {key.replace(/_/g, " ")}:
                        </td>
                        <td>{value || "-"}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setViewingAtivo(null)}
                >
                  Fechar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modal de Editar */}
      {editMode && editingAtivo && (
        <div className="modal fade show d-block" style={{ background: "rgba(0, 0, 0, 0.5)" }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header bg-dark text-white">
                <h5 className="modal-title">Editar Ativo</h5>
                <button
                  className="btn-close btn-close-white"
                  onClick={() => setEditMode(false)}
                ></button>
              </div>
              <div className="modal-body">
                <div className="row">
                  {Object.keys(editingAtivo).map((key) =>
                    key === "id" ? null : (
                      <div className="col-md-6 mb-3" key={key}>
                        <label className="form-label text-capitalize">
                          {key.replace(/_/g, " ")}
                        </label>
                        {typeof editingAtivo[key] === "boolean" ? (
                          <input
                            type="checkbox"
                            className="form-check-input"
                            checked={editingAtivo[key]}
                            onChange={(e) =>
                              setEditingAtivo({ ...editingAtivo, [key]: e.target.checked })
                            }
                          />
                        ) : (
                          <input
                            type="text"
                            className="form-control"
                            value={editingAtivo[key] || ""}
                            onChange={(e) =>
                              setEditingAtivo({ ...editingAtivo, [key]: e.target.value })
                            }
                          />
                        )}
                      </div>
                    )
                  )}
                </div>
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setEditMode(false)}
                >
                  Cancelar
                </button>
                <button className="btn btn-success" onClick={handleSaveEdit}>
                  Salvar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AtivoList;
