import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/token/', {
        username,
        password,
      });
      localStorage.setItem('access_token', response.data.access);
      localStorage.setItem('refresh_token', response.data.refresh);
      navigate('/');
    } catch (err) {
      setError('Credenciais inválidas. Tente novamente.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-700 via-purple-800 to-purple-900">
      <div className="w-full max-w-md bg-white shadow-xl rounded-lg p-8 animate-fade-in">
        {/* Logo */}
        <div className="flex justify-center mb-6">
          <img
            src="https://www.levesaude.com.br/build/assets/logo-menu-leve-5752d94b.png"
            alt="Leve Saúde"
            className="h-20 transform hover:scale-110 transition-transform duration-300"
          />
        </div>

        {/* Título */}
        <h1 className="text-2xl font-bold text-center text-gray-800 mb-8">
          Acesse a sua Conta
        </h1>

        {/* Formulário centralizado */}
        <div className="flex flex-col items-center">
          <form onSubmit={handleLogin} className="w-full space-y-6">
            {/* Campo Usuário */}
            <div className="flex flex-col items-center">
              <div className="w-4/5">
                <input
                  id="username"
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition duration-300"
                  placeholder="Digite seu usuário"
                  required
                />
              </div>
            </div>

            {/* Campo Senha */}
            <div className="flex flex-col items-center">
              <div className="w-4/5">
                <input
                  id="password"
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-purple-500 transition duration-300"
                  placeholder="Digite sua senha"
                  required
                />
              </div>
            </div>

            {/* Mensagem de Erro */}
            {error && (
              <div className="flex justify-center animate-shake">
                <div className="w-4/5">
                  <p className="text-red-500 text-sm text-center bg-red-50 p-3 rounded-lg border border-red-300">
                    {error}
                  </p>
                </div>
              </div>
            )}

            {/* Botão de Enviar centralizado */}
            <div className="flex justify-center">
              <button
                type="submit"
                className="w-4/5 bg-gradient-to-r from-gray-700 to-gray-900 text-white py-3 rounded-lg font-medium shadow-md hover:bg-gray-800 hover:shadow-lg transform hover:scale-105 transition-transform duration-300 focus:outline-none focus:ring-2 focus:ring-gray-500"
              >
                Entrar
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
