import React, { useEffect, useState, useCallback } from "react";
import api from "../services/api";

const AtivoList = () => {
  const [ativos, setAtivos] = useState([]);
  const [filteredAtivos, setFilteredAtivos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("todos");
  const [viewingAtivo, setViewingAtivo] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editingAtivo, setEditingAtivo] = useState(null);

  const loadAtivos = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get("ativos/");
      setAtivos(response.data);
      setFilteredAtivos(response.data);
    } catch (error) {
      console.error("Erro ao buscar ativos:", error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadAtivos();
  }, [loadAtivos]);

  const handleSaveEdit = async () => {
    try {
      await api.put(`ativos/${editingAtivo.id}/`, editingAtivo);
      setEditMode(false);
      setEditingAtivo(null);
      loadAtivos();
    } catch (error) {
      console.error("Erro ao editar ativo:", error);
    }
  };

  return (
    <div className="container mt-4">
      <div className="card shadow-sm">
        <div className="card-header bg-light text-dark py-3 d-flex justify-content-between align-items-center">
          <h2 className="h4 fw-bold mb-0">Lista de Ativos</h2>
          <div className="d-flex gap-3">
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="todos">Todas as Categorias</option>
              <option value="Notebook">Notebook</option>
              <option value="Desktop">Desktop</option>
            </select>
            <div className="input-group">
              <span className="input-group-text bg-light border-end-0">
                <i className="bi bi-search"></i>
              </span>
              <input
                type="text"
                className="form-control border-start-0 ps-0"
                placeholder="Pesquisar..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
        </div>
        <div className="card-body p-0">
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Carregando...</span>
              </div>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover mb-0 align-middle">
                <thead>
                  <tr className="bg-light">
                    <th>Nome</th>
                    <th>Patrimônio</th>
                    <th>Categoria</th>
                    <th>Responsável</th>
                    <th>Ações</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredAtivos.map((ativo) => (
                    <tr key={ativo.id}>
                      <td>{ativo.nome}</td>
                      <td>{ativo.codigo_patrimonio}</td>
                      <td>{ativo.categoria}</td>
                      <td>{ativo.responsavel}</td>
                      <td className="d-flex gap-2">
                        <button
                          className="btn btn-sm btn-warning"
                          onClick={() => {
                            setEditMode(true);
                            setEditingAtivo(ativo);
                          }}
                        >
                          Editar
                        </button>
                        <button
                          className="btn btn-sm btn-danger"
                          onClick={() => console.log("Excluir funcionalidade")}
                        >
                          Excluir
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Modal para edição */}
      {editMode && editingAtivo && (
        <div className="modal fade show d-block" style={{ background: "rgba(0, 0, 0, 0.5)" }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Editar Ativo</h5>
                <button
                  className="btn-close"
                  onClick={() => setEditMode(false)}
                ></button>
              </div>
              <div className="modal-body">
                <div className="row">
                  {Object.keys(editingAtivo).map((key) =>
                    key !== "id" ? ( // Exclui a seção de ID
                      <div className="col-md-6 mb-3" key={key}>
                        <label className="form-label text-capitalize">
                          {key.replace(/_/g, " ")}
                        </label>
                        <input
                          type="text"
                          className="form-control"
                          value={editingAtivo[key] || ""}
                          onChange={(e) =>
                            setEditingAtivo({ ...editingAtivo, [key]: e.target.value })
                          }
                        />
                      </div>
                    ) : null
                  )}
                </div>
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setEditMode(false)}
                >
                  Cancelar
                </button>
                <button
                  className="btn btn-success"
                  onClick={handleSaveEdit}
                >
                  Salvar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AtivoList;
