import React, { useState, useEffect } from "react";
import api from "../services/api";

const AtivoFormModal = () => {
    const [ativos, setAtivos] = useState([]);
    const [selectedAtivo, setSelectedAtivo] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [deletedAtivos, setDeletedAtivos] = useState([]);
    const [formData, setFormData] = useState(initialFormData());

    // Buscar ativos ao carregar o componente
    useEffect(() => {
        fetchAtivos();
    }, []);

    const fetchAtivos = async () => {
        try {
            const response = await api.get("ativos/");
            setAtivos(response.data);
        } catch (error) {
            console.error("Erro ao buscar ativos:", error);
        }
    };

    const handleChange = ({ target: { name, value, type, checked } }) => {
        setFormData(prevData => ({
            ...prevData,
            [name]: type === "checkbox" ? checked : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const action = isEditing ? api.put : api.post;
            const message = isEditing ? "Ativo atualizado com sucesso!" : "Ativo adicionado com sucesso!";
            await action(`ativos/${isEditing ? selectedAtivo.id + '/' : ''}`, formData);
            alert(message);
            resetForm();
            fetchAtivos();
        } catch (error) {
            alert(isEditing ? "Erro ao atualizar ativo." : "Erro ao adicionar ativo.");
        }
    };

    const handleDelete = async (ativo) => {
        if (window.confirm("Tem certeza que deseja excluir este ativo?")) {
            try {
                await api.delete(`ativos/${ativo.id}/`);
                setDeletedAtivos(prev => [...prev, ativo]);
                fetchAtivos();
                alert("Ativo excluído com sucesso!");
            } catch (error) {
                alert("Erro ao excluir ativo.");
            }
        }
    };

    const handleUndo = async (ativo) => {
        try {
            await api.post("ativos/", ativo);
            setDeletedAtivos(prev => prev.filter(a => a.id !== ativo.id));
            fetchAtivos();
            alert("Ativo restaurado com sucesso!");
        } catch (error) {
            alert("Erro ao restaurar ativo.");
        }
    };

    const handleEdit = (ativo) => {
        setSelectedAtivo(ativo);
        setFormData(ativo);
        setIsEditing(true);
    };

    const resetForm = () => {
        setFormData(initialFormData());
        setIsEditing(false);
        setSelectedAtivo(null);
    };

    const initialFormData = () => ({
        nome: "",
        codigo_patrimonio: "",
        categoria: "Notebook",
        localidade: "",
        setor: "",
        responsavel: "",
        status: "Ativo",
        data_ultima_avaliacao: "",
        data_proxima_avaliacao: "",
        modelo: "",
        marca: "",
        numero_serie: "",
        mac_address_ethernet: "",
        mac_address_wifi: "",
        ip_fixado: "",
        bitlocker: false,
        antivirus_licenca: "",
        antivirus_versao: "",
        bart_wazuh: false,
        chrome_enterprise: false,
        acesso_remoto_id: "",
        termo_custodia: false,
    });

    return (
        <div className="container-fluid">
            {/* Lista de Ativos */}
            <div className="table-responsive mt-4">
                <table className="table table-striped table-hover">
                    <thead className="table-dark">
                        <tr>
                            <th>Nome</th>
                            <th>Código</th>
                            <th>Categoria</th>
                            <th>Setor</th>
                            <th>Status</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>
                        {ativos.map((ativo) => (
                            <tr key={ativo.id}>
                                <td>{ativo.nome}</td>
                                <td>{ativo.codigo_patrimonio}</td>
                                <td>{ativo.categoria}</td>
                                <td>{ativo.setor}</td>
                                <td>
                                    <span className={`badge ${ativo.status === 'Ativo' ? 'bg-success' : 'bg-danger'}`}>
                                        {ativo.status}
                                    </span>
                                </td>
                                <td>
                                    <button
                                        className="btn btn-info btn-sm me-2"
                                        data-bs-toggle="modal"
                                        data-bs-target="#viewAtivoModal"
                                        onClick={() => setSelectedAtivo(ativo)}
                                    >
                                        Ver
                                    </button>
                                    <button
                                        className="btn btn-warning btn-sm me-2"
                                        data-bs-toggle="modal"
                                        data-bs-target="#ativoFormModal"
                                        onClick={() => handleEdit(ativo)}
                                    >
                                        Editar
                                    </button>
                                    <button
                                        className="btn btn-danger btn-sm"
                                        onClick={() => handleDelete(ativo)}
                                    >
                                        Excluir
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Modal de Visualização */}
            <ModalView ativo={selectedAtivo} />

            {/* Modal de Formulário */}
            <ModalForm
                isEditing={isEditing}
                formData={formData}
                handleChange={handleChange}
                handleSubmit={handleSubmit}
                resetForm={resetForm}
            />

            {/* Botão Flutuante para Adicionar */}
            <FloatingButton resetForm={resetForm} />
        </div>
    );
};

const ModalView = ({ ativo }) => (
    <div className="modal fade" id="viewAtivoModal" tabIndex="-1" aria-hidden="true">
        <div className="modal-dialog modal-lg">
            <div className="modal-content">
                <div className="modal-header bg-info text-white">
                    <h5 className="modal-title">Detalhes do Ativo</h5>
                    <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                </div>
                <div className="modal-body">
                    {ativo && (
                        <div className="row g-3">
                            <div className="col-md-6">
                                <h6 className="text-muted">Informações Básicas</h6>
                                <p><strong>Nome:</strong> {ativo.nome}</p>
                                <p><strong>Código:</strong> {ativo.codigo_patrimonio}</p>
                                <p><strong>Categoria:</strong> {ativo.categoria}</p>
                                <p><strong>Status:</strong> {ativo.status}</p>
                            </div>
                            <div className="col-md-6">
                                <h6 className="text-muted">Localização</h6>
                                <p><strong>Localidade:</strong> {ativo.localidade}</p>
                                <p><strong>Setor:</strong> {ativo.setor}</p>
                                <p><strong>Responsável:</strong> {ativo.responsavel}</p>
                            </div>
                            <div className="col-md-6">
                                <h6 className="text-muted">Especificações</h6>
                                <p><strong>Modelo:</strong> {ativo.modelo}</p>
                                <p><strong>Marca:</strong> {ativo.marca}</p>
                                <p><strong>Número de Série:</strong> {ativo.numero_serie}</p>
                            </div>
                            <div className="col-md-6">
                                <h6 className="text-muted">Rede</h6>
                                <p><strong>MAC Ethernet:</strong> {ativo.mac_address_ethernet}</p>
                                <p><strong>MAC WiFi:</strong> {ativo.mac_address_wifi}</p>
                                <p><strong>IP Fixo:</strong> {ativo.ip_fixado}</p>
                            </div>
                            <div className="col-12">
                                <h6 className="text-muted">Segurança</h6>
                                <div className="d-flex flex-wrap gap-3">
                                    <span className={`badge ${ativo.bitlocker ? 'bg-success' : 'bg-secondary'}`}>Bitlocker</span>
                                    <span className={`badge ${ativo.bart_wazuh ? 'bg-success' : 'bg-secondary'}`}>Bart/Wazuh</span>
                                    <span className={`badge ${ativo.chrome_enterprise ? 'bg-success' : 'bg-secondary'}`}>Chrome Enterprise</span>
                                    <span className={`badge ${ativo.termo_custodia ? 'bg-success' : 'bg-secondary'}`}>Termo de Custódia</span>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    </div>
);

const ModalForm = ({ isEditing, formData, handleChange, handleSubmit, resetForm }) => (
    <div className="modal fade" id="ativoFormModal" tabIndex="-1" aria-hidden="true">
        <div className="modal-dialog modal-lg">
            <div className="modal-content">
                <div className="modal-header" style={{ backgroundColor: "#451e44", color: "white" }}>
                    <h5 className="modal-title">{isEditing ? "Editar Ativo" : "Adicionar Novo Ativo"}</h5>
                    <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close" style={{ filter: "invert(1)" }}></button>
                </div>
                <div className="modal-body">
                    <form onSubmit={handleSubmit}>
                        <div className="row">
                            <div className="col-md-6 mb-3">
                                <label className="form-label">Nome do Ativo</label>
                                <input type="text" className="form-control" name="nome" value={formData.nome} onChange={handleChange} required />
                            </div>
                            <div className="col-md-6 mb-3">
                                <label className="form-label">Código de Patrimônio</label>
                                <input type="text" className="form-control" name="codigo_patrimonio" value={formData.codigo_patrimonio} onChange={handleChange} required />
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-md-4 mb-3">
                                <label className="form-label">Categoria</label>
                                <select className="form-select" name="categoria" value={formData.categoria} onChange={handleChange}>
                                    <option value="Notebook">Notebook</option>
                                    <option value="Desktop">Desktop</option>
                                    <option value="Smartphone">Smartphone</option>
                                    <option value="Tablet">Tablet</option>
                                    <option value="Monitor">Monitor</option>
                                </select>
                            </div>
                            <div className="col-md-4 mb-3">
                                <label className="form-label">Localidade</label>
                                <input type="text" className="form-control" name="localidade" value={formData.localidade} onChange={handleChange} />
                            </div>
                            <div className="col-md-4 mb-3">
                                <label className="form-label">Setor</label>
                                <input type="text" className="form-control" name="setor" value={formData.setor} onChange={handleChange} />
                            </div>
                        </div>
                        {/* Resto do formulário permanece o mesmo */}
                        {/* ... (mantendo o restante do código do formulário) ... */}
                        <div className="d-flex justify-content-end mt-4">
                            <button type="submit" className="btn btn-success me-2">{isEditing ? "Atualizar" : "Salvar"}</button>
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal" onClick={resetForm}>Cancelar</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
);

const FloatingButton = ({ resetForm }) => (
    <button
        type="button"
        className="btn btn-primary rounded-circle position-fixed"
        style={{
            bottom: "20px",
            right: "20px",
            width: "60px",
            height: "60px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "#451e44",
            borderColor: "#000",
            boxShadow: "0 2px 5px rgba(0,0,0,0.2)",
        }}
        data-bs-toggle="modal"
        data-bs-target="#ativoFormModal"
        onClick={resetForm}
    >
        <i className="bi bi-plus" style={{ fontSize: "1.5rem", color: "white" }}></i>
    </button>
);

export default AtivoFormModal;
