import React, { useEffect, useState, useCallback } from "react";
import api from "../services/api";

const AtivoList = () => {
  const [ativos, setAtivos] = useState([]);
  const [filteredAtivos, setFilteredAtivos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("todos");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "ascending" });
  const [viewingAtivo, setViewingAtivo] = useState(null);

  const loadAtivos = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get("ativos/");
      setAtivos(response.data);
      setFilteredAtivos(response.data);
    } catch (error) {
      console.error("Erro ao buscar ativos:", error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadAtivos();
  }, [loadAtivos]);

  const handleViewDetails = (ativo) => {
    setViewingAtivo(ativo);
  };

  return (
    <div className="container mt-4">
      <div className="card shadow-sm">
        <div className="card-header bg-white py-3 d-flex justify-content-between align-items-center">
          <h2 className="h4 fw-bold mb-0">Lista de Ativos</h2>
          <div className="d-flex gap-3">
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="todos">Todas as Categorias</option>
              <option value="Notebook">Notebook</option>
              <option value="Desktop">Desktop</option>
            </select>
            <div className="input-group">
              <span className="input-group-text bg-light border-end-0">
                <i className="bi bi-search"></i>
              </span>
              <input
                type="text"
                className="form-control border-start-0 ps-0"
                placeholder="Pesquisar..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
        </div>
        <div className="card-body p-0">
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Carregando...</span>
              </div>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover mb-0 align-middle">
                <thead>
                  <tr className="bg-light">
                    <th>Nome</th>
                    <th>Patrimônio</th>
                    <th>Categoria</th>
                    <th>Responsável</th>
                    <th>Ações</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredAtivos.map((ativo) => (
                    <tr key={ativo.id}>
                      <td>{ativo.nome}</td>
                      <td>{ativo.codigo_patrimonio}</td>
                      <td>{ativo.categoria}</td>
                      <td>{ativo.responsavel}</td>
                      <td>
                        <button
                          className="btn btn-sm btn-info me-2"
                          onClick={() => handleViewDetails(ativo)}
                        >
                          Visualizar Detalhes
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {viewingAtivo && (
        <div className="modal fade show d-block" style={{ background: "rgba(0, 0, 0, 0.5)" }}>
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Detalhes do Ativo</h5>
                <button
                  className="btn-close"
                  onClick={() => setViewingAtivo(null)}
                ></button>
              </div>
              <div className="modal-body">
                {Object.keys(viewingAtivo).map((key) => (
                  <p key={key}>
                    <strong>{key.replace(/_/g, " ")}:</strong> {viewingAtivo[key] || "-"}
                  </p>
                ))}
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setViewingAtivo(null)}
                >
                  Fechar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AtivoList;
