    nome: "",
    codigo_patrimonio: "",
    categoria: "Notebook",
    localidade: "",
    setor: "",
    responsavel: "",
    status: "Ativo",
    data_ultima_avaliacao: "",
    data_proxima_avaliacao: "",
    modelo: "",
    marca: "",
    numero_serie: "",
    mac_address_ethernet: "",
    mac_address_wifi: "",
    ip_fixado: "",
    bitlocker: false,
    antivirus_licenca: "",
    antivirus_versao: "",
    bart_wazuh: false,
    chrome_enterprise: false,
    acesso_remoto_id: "",
    termo_custodia: false,
};

const AtivoFormModal = () => {
    const [ativos, setAtivos] = useState([]);
    const [selectedAtivo, setSelectedAtivo] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState(defaultFormData);

    useEffect(() => {
        fetchAtivos();
    }, []);

    const fetchAtivos = async () => {
        try {
            const response = await api.get("ativos/");
            setAtivos(response.data);
        } catch (error) {
            console.error("Erro ao buscar ativos:", error);
        }
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: type === "checkbox" ? checked : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditing) {
                await api.put(`ativos/${selectedAtivo.id}/`, formData);
                alert("Ativo atualizado com sucesso!");
            } else {
                await api.post("ativos/", formData);
                alert("Ativo adicionado com sucesso!");
            }
            resetForm();
            fetchAtivos();
        } catch (error) {
            alert("Erro ao salvar ativo.");
        }
    };

    const handleDelete = async (ativo) => {
        if (window.confirm("Tem certeza que deseja excluir este ativo?")) {
            try {
                await api.delete(`ativos/${ativo.id}/`);
                fetchAtivos();
                alert("Ativo excluÃ­do com sucesso!");
            } catch (error) {
                alert("Erro ao excluir ativo.");
            }
        }
    };

    const handleEdit = (ativo) => {
        setSelectedAtivo(ativo);
        setFormData(ativo);
        setIsEditing(true);
    };

    const handleView = (ativo) => {
        setSelectedAtivo(ativo);
    };

    const resetForm = () => {
        setFormData(defaultFormData);
        setIsEditing(false);
        setSelectedAtivo(null);
    };

    return (
        <div className="container-fluid">
            <div className="table-responsive mt-4">
                <table className="table table-striped table-hover">
                    <thead className="table-dark">
                        <tr>
                            <th>Nome</th>
                            <th>CÃ³digo</th>
                            <th>Categoria</th>
                            <th>ResponsÃ¡vel</th>
                            <th>Ãltima AvaliaÃ§Ã£o</th>
                            <th>PrÃ³xima AvaliaÃ§Ã£o</th>
                            <th>AÃ§Ãµes</th>
                        </tr>
                    </thead>
                    <tbody>
                        {ativos.map((ativo) => (
                            <tr key={ativo.id}>
                                <td>{ativo.nome}</td>
                                <td>{ativo.codigo_patrimonio}</td>
                                <td>{ativo.categoria}</td>
                                <td>{ativo.responsavel}</td>
                                <td>{ativo.data_ultima_avaliacao}</td>
                                <td>{ativo.data_proxima_avaliacao}</td>
                                <td>
                                    <button
                                        className="btn btn-info btn-sm me-2"
                                        data-bs-toggle="modal"
                                        data-bs-target="#viewAtivoModal"
                                        onClick={() => handleView(ativo)}
                                    >
                                        Ver
                                    </button>
                                    <button
                                        className="btn btn-warning btn-sm me-2"
                                        data-bs-toggle="modal"
                                        data-bs-target="#ativoFormModal"
                                        onClick={() => handleEdit(ativo)}
                                    >
                                        Editar
                                    </button>
                                    <button
                                        className="btn btn-danger btn-sm"
                                        onClick={() => handleDelete(ativo)}
                                    >
                                        Excluir
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Modal de VisualizaÃ§Ã£o */}
            <div
                className="modal fade"
                id="viewAtivoModal"
                tabIndex="-1"
                aria-hidden="true"
            >
                <div className="modal-dialog modal-lg">
                    <div className="modal-content">
                        <div className="modal-header bg-info text-white">
                            <h5 className="modal-title">Detalhes do Ativo</h5>
                            <button
                                type="button"
                                className="btn-close"
                                data-bs-dismiss="modal"
                                aria-label="Close"
                            ></button>
                        </div>
                        <div className="modal-body">
                            {selectedAtivo && (
                                <div>
                                    <p><strong>Nome:</strong> {selectedAtivo.nome}</p>
                                    <p><strong>CÃ³digo de PatrimÃ´nio:</strong> {selectedAtivo.codigo_patrimonio}</p>
                                    <p><strong>Categoria:</strong> {selectedAtivo.categoria}</p>
                                    <p><strong>ResponsÃ¡vel:</strong> {selectedAtivo.responsavel}</p>
                                    <p><strong>Status:</strong> {selectedAtivo.status}</p>
                                    <p><strong>Modelo:</strong> {selectedAtivo.modelo}</p>
                                    <p><strong>Marca:</strong> {selectedAtivo.marca}</p>
                                    <p><strong>NÃºmero de SÃ©rie:</strong> {selectedAtivo.numero_serie}</p>
                                    <p><strong>Ãltima AvaliaÃ§Ã£o:</strong> {selectedAtivo.data_ultima_avaliacao}</p>
                                    <p><strong>PrÃ³xima AvaliaÃ§Ã£o:</strong> {selectedAtivo.data_proxima_avaliacao}</p>
                                    <p><strong>IP Fixado:</strong> {selectedAtivo.ip_fixado}</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>

            {/* Modal de FormulÃ¡rio */}
            <div
                className="modal fade"
                id="ativoFormModal"
                tabIndex="-1"
                aria-hidden="true"
            >
                <div className="modal-dialog modal-lg">
                    <div className="modal-content">
                        <div className="modal-header bg-primary text-white">
                            <h5 className="modal-title">
                                {isEditing ? "Editar Ativo" : "Adicionar Novo Ativo"}
                            </h5>
                            <button
                                type="button"
                                className="btn-close"
                                data-bs-dismiss="modal"
                                aria-label="Close"
                            ></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={handleSubmit}>
                                <div className="row">
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">Nome do Ativo</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="nome"
                                            value={formData.nome}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">CÃ³digo de PatrimÃ´nio</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="codigo_patrimonio"
                                            value={formData.codigo_patrimonio}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>
                                </div>
                                <div className="row">
                                    <div className="col-md-4 mb-3">
                                        <label className="form-label">Categoria</label>
                                        <select
                                            className="form-select"
                                            name="categoria"
                                            value={formData.categoria}
                                            onChange={handleChange}
                                        >
                                            <option value="Notebook">Notebook</option>
                                            <option value="Desktop">Desktop</option>
                                            <option value="Smartphone">Smartphone</option>
                                            <option value="Tablet">Tablet</option>
                                            <option value="Monitor">Monitor</option>
                                        </select>
                                    </div>
                                    <div className="col-md-4 mb-3">
                                        <label className="form-label">Setor</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="setor"
                                            value={formData.setor}
                                            onChange={handleChange}
                                        />
                                    </div>
                                    <div className="col-md-4 mb-3">
                                        <label className="form-label">ResponsÃ¡vel</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="responsavel"
                                            value={formData.responsavel}
                                            onChange={handleChange}
                                        />
                                    </div>
                                </div>
                                <div className="d-flex justify-content-end">
                                    <button type="submit" className="btn btn-success me-2">
                                        {isEditing ? "Atualizar" : "Salvar"}
                                    </button>
                                    <button
                                        type="button"
                                        className="btn btn-secondary"
                                        data-bs-dismiss="modal"
                                    >
                                        Cancelar
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AtivoFormModal;
