import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:8000/api/token/', {
        username,
        password,
      });
      localStorage.setItem('access_token', response.data.access);
      localStorage.setItem('refresh_token', response.data.refresh);
      navigate('/');
    } catch (err) {
      setError('Credenciais inválidas. Tente novamente.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-black">
      <div className="bg-neutral-900 rounded-lg p-6 w-96 shadow-lg">
        <h1 className="text-center text-white text-3xl font-bold mb-6">
          Instagram
        </h1>
        <form onSubmit={handleLogin} className="space-y-4">
          {/* Campo Usuário */}
          <input
            id="username"
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full p-3 rounded bg-neutral-800 text-white placeholder-gray-400 border border-neutral-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Telefone, nome de usuário ou email"
            required
          />

          {/* Campo Senha */}
          <input
            id="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-3 rounded bg-neutral-800 text-white placeholder-gray-400 border border-neutral-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
            placeholder="Senha"
            required
          />

          {/* Mensagem de erro */}
          {error && (
            <p className="text-red-500 text-sm text-center">
              {error}
            </p>
          )}

          {/* Checkbox */}
          <div className="flex items-center space-x-2 text-gray-400">
            <input
              type="checkbox"
              id="remember"
              className="form-checkbox bg-neutral-800 text-blue-500 focus:ring-0 focus:ring-offset-0"
            />
            <label htmlFor="remember" className="text-sm">
              Salvar informações de login
            </label>
          </div>

          {/* Botão de Login */}
          <button
            type="submit"
            className="w-full p-3 rounded bg-blue-500 text-white font-bold hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-300"
          >
            Entrar
          </button>
        </form>
      </div>
    </div>
  );
}

export default Login;
