import React, { useEffect, useState, useCallback } from "react";
import api from "../services/api";

const AtivoList = () => {
  const [ativos, setAtivos] = useState([]);
  const [filteredAtivos, setFilteredAtivos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("todos");
  const [sortConfig, setSortConfig] = useState({ key: null, direction: "ascending" });
  const [editMode, setEditMode] = useState(false);
  const [editingAtivo, setEditingAtivo] = useState(null);
  const [deletedAtivo, setDeletedAtivo] = useState(null);

  // Load assets from the API
  const loadAtivos = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get("ativos/");
      setAtivos(response.data);
      setFilteredAtivos(response.data);
    } catch (error) {
      console.error("Erro ao buscar ativos:", error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadAtivos();
  }, [loadAtivos]);

  // Filter ativos based on search and category
  useEffect(() => {
    const filtered = ativos.filter((ativo) => {
      const matchesSearch =
        ativo.nome?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        ativo.codigo_patrimonio?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        ativo.categoria?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        ativo.responsavel?.toLowerCase().includes(searchTerm.toLowerCase());

      const matchesCategory = selectedCategory === "todos" || ativo.categoria === selectedCategory;

      return matchesSearch && matchesCategory;
    });

    setFilteredAtivos(filtered);
  }, [searchTerm, selectedCategory, ativos]);

  // Handle sorting by key
  const handleSort = (key) => {
    const direction = sortConfig.key === key && sortConfig.direction === "ascending" ? "descending" : "ascending";
    setSortConfig({ key, direction });

    const sortedAtivos = [...filteredAtivos].sort((a, b) => {
      if (a[key] < b[key]) return direction === "ascending" ? -1 : 1;
      if (a[key] > b[key]) return direction === "ascending" ? 1 : -1;
      return 0;
    });

    setFilteredAtivos(sortedAtivos);
  };

  const formatDate = (dateString) => (!dateString ? "-" : new Date(dateString).toLocaleDateString("pt-BR"));

  const handleSaveEdit = async () => {
    try {
      await api.put(`ativos/${editingAtivo.id}/`, editingAtivo);
      setEditMode(false);
      setEditingAtivo(null);
      loadAtivos();
    } catch (error) {
      console.error("Erro ao editar ativo:", error);
    }
  };

  const handleDelete = async (ativo) => {
    try {
      await api.delete(`ativos/${ativo.id}/`);
      setDeletedAtivo(ativo);
      setAtivos(ativos.filter((a) => a.id !== ativo.id));
    } catch (error) {
      console.error("Erro ao excluir ativo:", error);
    }
  };

  const handleUndoDelete = async () => {
    try {
      await api.post("ativos/", deletedAtivo);
      setDeletedAtivo(null);
      loadAtivos();
    } catch (error) {
      console.error("Erro ao desfazer exclusÃ£o:", error);
    }
  };

  return (
    <div className="container mt-4">
      <div className="card shadow-sm">
        <div className="card-header bg-white py-3 d-flex justify-content-between align-items-center">
          <h2 className="h4 fw-bold mb-0">Lista de Ativos</h2>
          <div className="d-flex gap-3">
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="todos">Todas as Categorias</option>
              <option value="Notebook">Notebook</option>
              <option value="Desktop">Desktop</option>
              <option value="Smartphone">Smartphone</option>
              <option value="Monitor">Monitor</option>
            </select>
            <div className="input-group">
              <span className="input-group-text bg-light border-end-0">
                <i className="bi bi-search"></i>
              </span>
              <input
                type="text"
                className="form-control border-start-0 ps-0"
                placeholder="Pesquisar..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
        </div>

        <div className="card-body p-0">
          {loading ? (
            <div className="text-center py-5">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Carregando...</span>
              </div>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover mb-0 align-middle">
                <thead>
                  <tr className="bg-light">
                    <th className="px-4 py-3">Nome</th>
                    <th className="px-4 py-3">PatrimÃ´nio</th>
                    <th className="px-4 py-3">Categoria</th>
                    <th className="px-4 py-3">ResponsÃ¡vel</th>
                    <th className="px-4 py-3">Ãltima AvaliaÃ§Ã£o</th>
                    <th className="px-4 py-3">PrÃ³xima AvaliaÃ§Ã£o</th>
                    <th className="px-4 py-3">AÃ§Ãµes</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredAtivos.length === 0 ? (
                    <tr>
                      <td colSpan="7" className="text-center py-4 text-muted">
                        Nenhum ativo encontrado
                      </td>
                    </tr>
                  ) : (
                    filteredAtivos.map((ativo) => (
                      <tr key={ativo.id}>
                        <td className="px-4 py-3">{ativo.nome}</td>
                        <td className="px-4 py-3">{ativo.codigo_patrimonio}</td>
                        <td className="px-4 py-3">{ativo.categoria}</td>
                        <td className="px-4 py-3">{ativo.responsavel}</td>
                        <td className="px-4 py-3">{formatDate(ativo.data_ultima_avaliacao)}</td>
                        <td className="px-4 py-3">{formatDate(ativo.data_proxima_avaliacao)}</td>
                        <td className="px-4 py-3">
                          <button className="btn btn-sm btn-warning me-2">Editar</button>
                          <button className="btn btn-sm btn-danger">Excluir</button>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AtivoList;
