import React, { useState, useEffect, useCallback } from "react";
import api from "../services/api";

const defaultFormData = {
    nome: "",
    codigo_patrimonio: "",
    categoria: "Notebook",
    localidade: "",
    setor: "",
    responsavel: "",
    status: "Ativo",
    data_ultima_avaliacao: "",
    data_proxima_avaliacao: "",
    modelo: "",
    marca: "",
    numero_serie: "",
    mac_address_ethernet: "",
    mac_address_wifi: "",
    ip_fixado: "",
    bitlocker: false,
    antivirus_licenca: "",
    antivirus_versao: "",
    bart_wazuh: false,
    chrome_enterprise: false,
    acesso_remoto_id: "",
    termo_custodia: false,
};

const AtivoFormModal = () => {
    const [ativos, setAtivos] = useState([]);
    const [selectedAtivo, setSelectedAtivo] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState(defaultFormData);

    const fetchAtivos = useCallback(async () => {
        try {
            const response = await api.get("ativos/");
            setAtivos(response.data);
        } catch (error) {
            console.error("Erro ao buscar ativos:", error);
        }
    }, []);

    useEffect(() => {
        fetchAtivos();
    }, [fetchAtivos]);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: type === "checkbox" ? checked : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditing) {
                await api.put(`ativos/${selectedAtivo.id}/`, formData);
                alert("Ativo atualizado com sucesso!");
            } else {
                await api.post("ativos/", formData);
                alert("Ativo adicionado com sucesso!");
            }
            resetForm();
            fetchAtivos();
        } catch (error) {
            alert("Erro ao salvar ativo.");
        }
    };

    const handleDelete = async (ativo) => {
        if (window.confirm("Tem certeza que deseja excluir este ativo?")) {
            try {
                await api.delete(`ativos/${ativo.id}/`);
                fetchAtivos();
                alert("Ativo excluído com sucesso!");
            } catch (error) {
                alert("Erro ao excluir ativo.");
            }
        }
    };

    const handleEdit = (ativo) => {
        setSelectedAtivo(ativo);
        setFormData(ativo);
        setIsEditing(true);
    };

    const resetForm = () => {
        setFormData(defaultFormData);
        setIsEditing(false);
        setSelectedAtivo(null);
    };

    const renderAtivoRow = (ativo) => (
        <tr key={ativo.id}>
            <td>{ativo.nome}</td>
            <td>{ativo.codigo_patrimonio}</td>
            <td>{ativo.categoria}</td>
            <td>{ativo.setor}</td>
            <td>
                <span
                    className={`badge ${ativo.status === "Ativo" ? "bg-success" : "bg-danger"}`}
                >
                    {ativo.status}
                </span>
            </td>
            <td>
                <button
                    className="btn btn-info btn-sm me-2"
                    onClick={() => setSelectedAtivo(ativo)}
                    data-bs-toggle="modal"
                    data-bs-target="#viewAtivoModal"
                >
                    Ver
                </button>
                <button
                    className="btn btn-warning btn-sm me-2"
                    onClick={() => handleEdit(ativo)}
                    data-bs-toggle="modal"
                    data-bs-target="#ativoFormModal"
                >
                    Editar
                </button>
                <button
                    className="btn btn-danger btn-sm"
                    onClick={() => handleDelete(ativo)}
                >
                    Excluir
                </button>
            </td>
        </tr>
    );

    return (
        <div className="container-fluid">
            <div className="table-responsive mt-4">
                <table className="table table-striped table-hover">
                    <thead className="table-dark">
                        <tr>
                            <th>Nome</th>
                            <th>Código</th>
                            <th>Categoria</th>
                            <th>Setor</th>
                            <th>Status</th>
                            <th>Ações</th>
                        </tr>
                    </thead>
                    <tbody>{ativos.map(renderAtivoRow)}</tbody>
                </table>
            </div>

            <div
                className="modal fade"
                id="viewAtivoModal"
                tabIndex="-1"
                aria-hidden="true"
            >
                <div className="modal-dialog modal-lg">
                    <div className="modal-content">
                        <div className="modal-header bg-info text-white">
                            <h5 className="modal-title">Detalhes do Ativo</h5>
                            <button
                                type="button"
                                className="btn-close"
                                data-bs-dismiss="modal"
                                aria-label="Close"
                            ></button>
                        </div>
                        <div className="modal-body">
                            {selectedAtivo && (
                                <div>
                                    <p><strong>Nome:</strong> {selectedAtivo.nome}</p>
                                    <p><strong>Código:</strong> {selectedAtivo.codigo_patrimonio}</p>
                                    <p><strong>Categoria:</strong> {selectedAtivo.categoria}</p>
                                    <p><strong>Status:</strong> {selectedAtivo.status}</p>
                                    <p><strong>Setor:</strong> {selectedAtivo.setor}</p>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>

            <div
                className="modal fade"
                id="ativoFormModal"
                tabIndex="-1"
                aria-hidden="true"
            >
                <div className="modal-dialog modal-lg">
                    <div className="modal-content">
                        <div className="modal-header bg-primary text-white">
                            <h5 className="modal-title">
                                {isEditing ? "Editar Ativo" : "Adicionar Novo Ativo"}
                            </h5>
                            <button
                                type="button"
                                className="btn-close"
                                data-bs-dismiss="modal"
                                aria-label="Close"
                            ></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label className="form-label">Nome do Ativo</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="nome"
                                        value={formData.nome}
                                        onChange={handleChange}
                                        required
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Código de Patrimônio</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="codigo_patrimonio"
                                        value={formData.codigo_patrimonio}
                                        onChange={handleChange}
                                        required
                                    />
                                </div>
                                <div className="d-flex justify-content-end">
                                    <button type="submit" className="btn btn-success me-2">
                                        {isEditing ? "Atualizar" : "Salvar"}
                                    </button>
                                    <button
                                        type="button"
                                        className="btn btn-secondary"
                                        data-bs-dismiss="modal"
                                    >
                                        Cancelar
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AtivoFormModal;
