import React, { useEffect, useState, useCallback } from "react";
import api from "../services/api";

const AtivoList = () => {
  const [ativos, setAtivos] = useState([]);
  const [filteredAtivos, setFilteredAtivos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("todos");
  const [viewingAtivo, setViewingAtivo] = useState(null);
  const [editMode, setEditMode] = useState(false);
  const [editingAtivo, setEditingAtivo] = useState(null);
  const [deletedAtivo, setDeletedAtivo] = useState(null);
  const [confirmDelete, setConfirmDelete] = useState(false);
  const [ativoToDelete, setAtivoToDelete] = useState(null);
  const [newAtivo, setNewAtivo] = useState({
    nome: "",
    codigo_patrimonio: "",
    categoria: "",
    responsavel: "",
  });

  const loadAtivos = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get("ativos/");
      setAtivos(response.data);
      setFilteredAtivos(response.data);
    } catch (error) {
      console.error("Erro ao buscar ativos:", error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadAtivos();
  }, [loadAtivos]);

  useEffect(() => {
    const filtered = ativos.filter((ativo) => {
      const matchesSearch = Object.values(ativo).some((value) =>
        String(value).toLowerCase().includes(searchTerm.toLowerCase())
      );

      const matchesCategory =
        selectedCategory === "todos" || ativo.categoria === selectedCategory;

      return matchesSearch && matchesCategory;
    });

    setFilteredAtivos(filtered);
  }, [searchTerm, selectedCategory, ativos]);

  const handleAddNewAtivo = async () => {
    try {
      const response = await api.post("ativos/", newAtivo);
      setAtivos((prevAtivos) => [...prevAtivos, response.data]);
      setFilteredAtivos((prevFilteredAtivos) => [...prevFilteredAtivos, response.data]);
      setNewAtivo({
        nome: "",
        codigo_patrimonio: "",
        categoria: "",
        responsavel: "",
      });
    } catch (error) {
      console.error("Erro ao adicionar novo ativo:", error);
    }
  };

  return (
    <div className="container mt-4">
      <div className="card shadow-sm">
        <div className="card-header bg-light text-dark py-3 d-flex justify-content-between align-items-center">
          <h2 className="h4 fw-bold mb-0">Lista de Ativos</h2>
          <div className="d-flex gap-3">
            <input
              type="text"
              className="form-control"
              placeholder="Pesquisar..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <select
              className="form-select"
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
            >
              <option value="todos">Todas as Categorias</option>
              <option value="Notebook">Notebook</option>
              <option value="Desktop">Desktop</option>
              <option value="Smartphone">Smartphone</option>
              <option value="Monitor">Monitor</option>
            </select>
          </div>
        </div>
        <div className="card-body">
          <div className="mb-3">
            <h5 className="fw-bold">Adicionar Novo Ativo</h5>
            <div className="row g-3">
              <div className="col-md-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Nome"
                  value={newAtivo.nome}
                  onChange={(e) =>
                    setNewAtivo({ ...newAtivo, nome: e.target.value })
                  }
                />
              </div>
              <div className="col-md-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Patrimônio"
                  value={newAtivo.codigo_patrimonio}
                  onChange={(e) =>
                    setNewAtivo({ ...newAtivo, codigo_patrimonio: e.target.value })
                  }
                />
              </div>
              <div className="col-md-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Responsável"
                  value={newAtivo.responsavel}
                  onChange={(e) =>
                    setNewAtivo({ ...newAtivo, responsavel: e.target.value })
                  }
                />
              </div>
              <div className="col-md-3">
                <select
                  className="form-select"
                  value={newAtivo.categoria}
                  onChange={(e) =>
                    setNewAtivo({ ...newAtivo, categoria: e.target.value })
                  }
                >
                  <option value="">Selecione a Categoria</option>
                  <option value="Notebook">Notebook</option>
                  <option value="Desktop">Desktop</option>
                  <option value="Smartphone">Smartphone</option>
                  <option value="Monitor">Monitor</option>
                </select>
              </div>
              <div className="col-12 text-end">
                <button className="btn btn-primary" onClick={handleAddNewAtivo}>
                  Adicionar Ativo
                </button>
              </div>
            </div>
          </div>
          <div className="table-responsive">
            <table className="table table-hover mb-0 align-middle">
              <thead>
                <tr className="bg-light">
                  <th>Nome</th>
                  <th>Patrimônio</th>
                  <th>Categoria</th>
                  <th>Responsável</th>
                  <th>Ações</th>
                </tr>
              </thead>
              <tbody>
                {filteredAtivos.map((ativo) => (
                  <tr key={ativo.id}>
                    <td>{ativo.nome}</td>
                    <td>{ativo.codigo_patrimonio}</td>
                    <td>{ativo.categoria}</td>
                    <td>{ativo.responsavel}</td>
                    <td className="d-flex gap-2">
                      <button
                        className="btn btn-sm btn-info"
                        onClick={() => setViewingAtivo(ativo)}
                      >
                        Visualizar Detalhes
                      </button>
                      <button
                        className="btn btn-sm btn-warning"
                        onClick={() => {
                          setEditMode(true);
                          setEditingAtivo(ativo);
                        }}
                      >
                        Editar
                      </button>
                      <button
                        className="btn btn-sm btn-danger"
                        onClick={() => {
                          setConfirmDelete(true);
                          setAtivoToDelete(ativo);
                        }}
                      >
                        Excluir
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AtivoList;
