import React, { useState } from "react";
import api from "../services/api";

const AtivoFormModal = () => {
  const initialFormData = {
    nome: "",
    codigo_patrimonio: "",
    categoria: "Notebook",
    localidade: "",
    setor: "",
    responsavel: "",
    status: "Ativo",
    data_ultima_avaliacao: "",
    data_proxima_avaliacao: "",
    modelo: "",
    marca: "",
    numero_serie: "",
    mac_address_ethernet: "",
    mac_address_wifi: "",
    ip_fixado: "",
    bitlocker: false,
    antivirus_licenca: "",
    antivirus_versao: "",
    bart_wazuh: false,
    chrome_enterprise: false,
    acesso_remoto_id: "",
    termo_custodia: false,
  };

  const [formData, setFormData] = useState(initialFormData);

  const handleChange = ({ target: { name, value, type, checked } }) => {
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await api.post("ativos/", formData);
      alert("Ativo adicionado com sucesso!");
      setFormData(initialFormData);
    } catch (error) {
      alert("Erro ao adicionar ativo. Verifique os dados.");
    }
  };

  return (
    <div>
      {/* Botão para abrir o modal */}
      <button
        type="button"
        className="btn btn-primary rounded-circle position-fixed shadow"
        style={{
          bottom: "20px",
          right: "20px",
          width: "60px",
          height: "60px",
          backgroundColor: "#451e44",
          borderColor: "#000",
        }}
        data-bs-toggle="modal"
        data-bs-target="#ativoFormModal"
        aria-label="Adicionar Ativo"
      >
        <span style={{ fontSize: "24px", color: "#fff", fontWeight: "bold" }}>+</span>
      </button>

      {/* Modal */}
      <div
        className="modal fade"
        id="ativoFormModal"
        tabIndex="-1"
        aria-labelledby="ativoFormModalLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog modal-lg">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="ativoFormModalLabel">
                Adicionar Novo Ativo
              </h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Fechar"
              ></button>
            </div>
            <div className="modal-body">
              <form onSubmit={handleSubmit}>
                {/* Inputs gerados dinamicamente */}
                {[
                  {
                    label: "Nome do Ativo",
                    name: "nome",
                    type: "text",
                    required: true,
                  },
                  {
                    label: "Código de Patrimônio",
                    name: "codigo_patrimonio",
                    type: "text",
                    required: true,
                  },
                  { label: "Localidade", name: "localidade", type: "text" },
                  { label: "Setor", name: "setor", type: "text" },
                  { label: "Responsável", name: "responsavel", type: "text" },
                  { label: "Data da Última Avaliação", name: "data_ultima_avaliacao", type: "date" },
                  { label: "Data da Próxima Avaliação", name: "data_proxima_avaliacao", type: "date" },
                  { label: "Modelo", name: "modelo", type: "text" },
                  { label: "Marca", name: "marca", type: "text" },
                  { label: "Número de Série", name: "numero_serie", type: "text" },
                  { label: "MAC Address Ethernet", name: "mac_address_ethernet", type: "text" },
                  { label: "MAC Address Wifi", name: "mac_address_wifi", type: "text" },
                  { label: "IP Fixado", name: "ip_fixado", type: "text" },
                  { label: "Acesso Remoto - ID", name: "acesso_remoto_id", type: "text" },
                ].map(({ label, name, type, required }) => (
                  <div className="mb-3" key={name}>
                    <label className="form-label">{label}</label>
                    <input
                      type={type}
                      className="form-control"
                      name={name}
                      value={formData[name]}
                      onChange={handleChange}
                      required={required}
                    />
                  </div>
                ))}

                {/* Select Inputs */}
                <div className="row">
                  {[
                    {
                      label: "Categoria",
                      name: "categoria",
                      options: ["Notebook", "Desktop"],
                    },
                    {
                      label: "Status",
                      name: "status",
                      options: ["Ativo", "Inativo"],
                    },
                  ].map(({ label, name, options }) => (
                    <div className="col-md-6 mb-3" key={name}>
                      <label className="form-label">{label}</label>
                      <select
                        className="form-select"
                        name={name}
                        value={formData[name]}
                        onChange={handleChange}
                      >
                        {options.map((option) => (
                          <option key={option} value={option}>
                            {option}
                          </option>
                        ))}
                      </select>
                    </div>
                  ))}
                </div>

                {/* Checkbox Inputs */}
                <div className="row">
                  {[
                    { label: "Bitlocker", name: "bitlocker" },
                    { label: "Bart/Wazuh", name: "bart_wazuh" },
                    { label: "Chrome Enterprise", name: "chrome_enterprise" },
                    { label: "Termo de Custódia", name: "termo_custodia" },
                  ].map(({ label, name }) => (
                    <div className="col-md-6 form-check mb-3" key={name}>
                      <input
                        type="checkbox"
                        className="form-check-input"
                        name={name}
                        checked={formData[name]}
                        onChange={handleChange}
                      />
                      <label className="form-check-label">{label}</label>
                    </div>
                  ))}
                </div>

                {/* Botões */}
                <div className="d-flex justify-content-end">
                  <button type="submit" className="btn btn-success me-2">
                    Salvar
                  </button>
                  <button
                    type="button"
                    className="btn btn-secondary"
                    data-bs-dismiss="modal"
                  >
                    Cancelar
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AtivoFormModal;
