import React, { useEffect, useState } from "react";
import api from "../services/api";

const AtivoList = () => {
    const [ativos, setAtivos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState("");
    const [filteredAtivos, setFilteredAtivos] = useState([]);
    const [sortConfig, setSortConfig] = useState({ key: null, direction: 'ascending' });
    const [selectedRow, setSelectedRow] = useState(null);

    useEffect(() => {
        loadAtivos();
    }, []);

    const loadAtivos = async () => {
        try {
            setLoading(true);
            const response = await api.get("ativos/");
            setAtivos(response.data);
            setFilteredAtivos(response.data);
        } catch (error) {
            console.error("Erro ao buscar ativos:", error);
            showErrorMessage("Não foi possível carregar os ativos. Tente novamente mais tarde.");
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = (e) => {
        const searchValue = e.target.value.toLowerCase();
        setSearchTerm(searchValue);

        const filtered = ativos.filter((ativo) => 
            ativo.nome?.toLowerCase().includes(searchValue) ||
            ativo.codigo_patrimonio?.toLowerCase().includes(searchValue) ||
            ativo.categoria?.toLowerCase().includes(searchValue) ||
            ativo.responsavel?.toLowerCase().includes(searchValue)
        );
        
        setFilteredAtivos(filtered);
    };

    const handleSort = (key) => {
        let direction = 'ascending';
        if (sortConfig.key === key && sortConfig.direction === 'ascending') {
            direction = 'descending';
        }
        setSortConfig({ key, direction });

        const sortedAtivos = [...filteredAtivos].sort((a, b) => {
            if (a[key] < b[key]) return direction === 'ascending' ? -1 : 1;
            if (a[key] > b[key]) return direction === 'ascending' ? 1 : -1;
            return 0;
        });

        setFilteredAtivos(sortedAtivos);
    };

    const formatDate = (dateString) => {
        if (!dateString) return "-";
        return new Date(dateString).toLocaleDateString("pt-BR");
    };

    const getSortIcon = (key) => {
        if (sortConfig.key !== key) return "↕️";
        return sortConfig.direction === 'ascending' ? "↑" : "↓";
    };

    const getStatusClass = (dataProxima) => {
        if (!dataProxima) return '';
        const hoje = new Date();
        const proxima = new Date(dataProxima);
        const diffDias = Math.ceil((proxima - hoje) / (1000 * 60 * 60 * 24));

        if (diffDias < 0) return 'table-danger';
        if (diffDias <= 30) return 'table-warning';
        return '';
    };

    return (
        <div className="container mt-4">
            <div className="card shadow">
                <div className="card-header bg-white py-3 border-bottom">
                    <div className="row align-items-center g-3">
                        <div className="col-md-6">
                            <h2 className="h4 mb-0 fw-bold d-flex align-items-center">
                                <i className="bi bi-clipboard-data me-2"></i>
                                Lista de Ativos
                            </h2>
                        </div>
                        <div className="col-md-6">
                            <div className="input-group">
                                <span className="input-group-text bg-light border-end-0">
                                    <i className="bi bi-search text-muted"></i>
                                </span>
                                <input
                                    type="text"
                                    className="form-control border-start-0 ps-0"
                                    placeholder="Pesquisar por nome, patrimônio, categoria ou responsável..."
                                    value={searchTerm}
                                    onChange={handleSearch}
                                    style={{
                                        outline: 'none',
                                        boxShadow: 'none'
                                    }}
                                />
                                {searchTerm && (
                                    <button
                                        className="btn btn-outline-secondary border-start-0"
                                        type="button"
                                        onClick={() => {
                                            setSearchTerm("");
                                            setFilteredAtivos(ativos);
                                        }}
                                    >
                                        <i className="bi bi-x"></i>
                                    </button>
                                )}
                            </div>
                        </div>
                    </div>
                </div>
                <div className="card-body p-0">
                    {loading ? (
                        <div className="text-center py-5">
                            <div className="spinner-border text-primary" role="status">
                                <span className="visually-hidden">Carregando...</span>
                            </div>
                            <p className="text-muted mt-2">Carregando ativos...</p>
                        </div>
                    ) : (
                        <div className="table-responsive">
                            <table className="table table-hover mb-0 align-middle">
                                <thead>
                                    <tr className="bg-light">
                                        {[
                                            { key: 'nome', label: 'Nome' },
                                            { key: 'codigo_patrimonio', label: 'Patrimônio' },
                                            { key: 'categoria', label: 'Categoria' },
                                            { key: 'responsavel', label: 'Responsável' },
                                            { key: 'data_ultima_avaliacao', label: 'Última Avaliação' },
                                            { key: 'data_proxima_avaliacao', label: 'Próxima Avaliação' }
                                        ].map(({ key, label }) => (
                                            <th
                                                key={key}
                                                className="px-4 py-3 text-dark fw-semibold user-select-none"
                                                onClick={() => handleSort(key)}
                                                style={{ cursor: 'pointer' }}
                                            >
                                                <div className="d-flex align-items-center">
                                                    {label}
                                                    <span className="ms-1 text-muted small">
                                                        {getSortIcon(key)}
                                                    </span>
                                                </div>
                                            </th>
                                        ))}
                                    </tr>
                                </thead>
                                <tbody className="border-top-0">
                                    {filteredAtivos.length === 0 ? (
                                        <tr>
                                            <td colSpan="6" className="text-center py-5">
                                                <i className="bi bi-search display-4 text-muted mb-3"></i>
                                                <p className="text-muted mb-0">Nenhum ativo encontrado</p>
                                            </td>
                                        </tr>
                                    ) : (
                                        filteredAtivos.map((ativo) => (
                                            <tr
                                                key={ativo.id}
                                                className={`${getStatusClass(ativo.data_proxima_avaliacao)} ${
                                                    selectedRow === ativo.id ? 'table-active' : ''
                                                }`}
                                                onClick={() => setSelectedRow(ativo.id)}
                                                style={{ cursor: 'pointer' }}
                                            >
                                                <td className="px-4 py-3 fw-medium">{ativo.nome}</td>
                                                <td className="px-4 py-3">{ativo.codigo_patrimonio}</td>
                                                <td className="px-4 py-3">
                                                    <span className="badge bg-light text-dark border">
                                                        {ativo.categoria}
                                                    </span>
                                                </td>
                                                <td className="px-4 py-3">{ativo.responsavel}</td>
                                                <td className="px-4 py-3 text-muted">
                                                    {formatDate(ativo.data_ultima_avaliacao)}
                                                </td>
                                                <td className="px-4 py-3 text-muted">
                                                    {formatDate(ativo.data_proxima_avaliacao)}
                                                </td>
                                            </tr>
                                        ))
                                    )}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
                <div className="card-footer bg-white py-3 border-top">
                    <div className="d-flex justify-content-between align-items-center">
                        <small className="text-muted">
                            Total de ativos: {filteredAtivos.length}
                        </small>
                        <div className="d-flex align-items-center">
                            <span className="badge bg-danger me-2"></span>
                            <small className="text-muted me-3">Avaliação Atrasada</small>
                            <span className="badge bg-warning me-2"></span>
                            <small className="text-muted">Avaliação Próxima</small>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

// Estilos globais
const styles = `
.form-control:focus {
    border-color: #dee2e6;
    box-shadow: none;
}

.input-group-text {
    border-color: #dee2e6;
}

.table th {
