import React, { useState, useEffect } from "react";
import api from "../services/api";

const defaultFormData = {
    nome: "",
    codigo_patrimonio: "",
    categoria: "Notebook",
    localidade: "",
    setor: "",
    responsavel: "",
    status: "Ativo",
    data_ultima_avaliacao: "",
    data_proxima_avaliacao: "",
    modelo: "",
    marca: "",
    numero_serie: "",
    mac_address_ethernet: "",
    mac_address_wifi: "",
    ip_fixado: "",
    bitlocker: false,
    antivirus_licenca: "",
    antivirus_versao: "",
    bart_wazuh: false,
    chrome_enterprise: false,
    acesso_remoto_id: "",
    termo_custodia: false,
};

const AtivoFormModal = () => {
    const [ativos, setAtivos] = useState([]);
    const [selectedAtivo, setSelectedAtivo] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [formData, setFormData] = useState(defaultFormData);

    useEffect(() => {
        fetchAtivos();
    }, []);

    const fetchAtivos = async () => {
        try {
            const response = await api.get("ativos/");
            setAtivos(response.data);
        } catch (error) {
            console.error("Erro ao buscar ativos:", error);
        }
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: type === "checkbox" ? checked : value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditing) {
                await api.put(`ativos/${selectedAtivo.id}/`, formData);
                alert("Ativo atualizado com sucesso!");
            } else {
                await api.post("ativos/", formData);
                alert("Ativo adicionado com sucesso!");
            }
            resetForm();
            fetchAtivos();
        } catch (error) {
            alert("Erro ao salvar ativo.");
        }
    };

    const handleDelete = async (ativo) => {
        if (window.confirm("Tem certeza que deseja excluir este ativo?")) {
            try {
                await api.delete(`ativos/${ativo.id}/`);
                fetchAtivos();
                alert("Ativo excluÃ­do com sucesso!");
            } catch (error) {
                alert("Erro ao excluir ativo.");
            }
        }
    };

    const handleEdit = (ativo) => {
        setSelectedAtivo(ativo);
        setFormData(ativo);
        setIsEditing(true);
    };

    const resetForm = () => {
        setFormData(defaultFormData);
        setIsEditing(false);
        setSelectedAtivo(null);
    };

    return (
        <div className="container-fluid">
            {/* Lista de Ativos */}
            <AtivoTable
                ativos={ativos}
                onEdit={handleEdit}
                onDelete={handleDelete}
                onView={setSelectedAtivo}
            />

            {/* Modal de VisualizaÃ§Ã£o */}
            {selectedAtivo && (
                <Modal
                    id="viewAtivoModal"
                    title="Detalhes do Ativo"
                    onClose={() => setSelectedAtivo(null)}
                >
                    <AtivoDetails ativo={selectedAtivo} />
                </Modal>
            )}

            {/* Modal de FormulÃ¡rio */}
            <Modal
                id="ativoFormModal"
                title={isEditing ? "Editar Ativo" : "Adicionar Novo Ativo"}
                onClose={resetForm}
            >
                <FormAtivo
                    formData={formData}
                    onChange={handleChange}
                    onSubmit={handleSubmit}
                />
            </Modal>

            {/* BotÃ£o Flutuante */}
            <FloatingButton
                icon="bi bi-plus"
                onClick={() => {
                    resetForm();
                    setSelectedAtivo(null);
                }}
            />
        </div>
    );
};

const AtivoTable = ({ ativos, onEdit, onDelete, onView }) => (
    <div className="table-responsive mt-4">
        <table className="table table-striped table-hover">
            <thead className="table-dark">
                <tr>
                    <th>Nome</th>
                    <th>CÃ³digo</th>
                    <th>Categoria</th>
                    <th>Setor</th>
                    <th>Status</th>
                    <th>AÃ§Ãµes</th>
                </tr>
            </thead>
            <tbody>
                {ativos.map((ativo) => (
                    <AtivoRow
                        key={ativo.id}
                        ativo={ativo}
                        onEdit={onEdit}
                        onDelete={onDelete}
                        onView={onView}
                    />
                ))}
            </tbody>
        </table>
    </div>
);

const AtivoRow = ({ ativo, onEdit, onDelete, onView }) => (
    <tr>
        <td>{ativo.nome}</td>
        <td>{ativo.codigo_patrimonio}</td>
        <td>{ativo.categoria}</td>
        <td>{ativo.setor}</td>
        <td>
            <span className={`badge ${ativo.status === "Ativo" ? "bg-success" : "bg-danger"}`}>
                {ativo.status}
            </span>
        </td>
        <td>
            <button
                className="btn btn-info btn-sm me-2"
                onClick={() => onView(ativo)}
            >
                Ver
            </button>
            <button
                className="btn btn-warning btn-sm me-2"
                onClick={() => onEdit(ativo)}
            >
                Editar
            </button>
            <button
                className="btn btn-danger btn-sm"
                onClick={() => onDelete(ativo)}
            >
                Excluir
            </button>
        </td>
    </tr>
);

const AtivoDetails = ({ ativo }) => (
    <div>
        <h6 className="text-muted">InformaÃ§Ãµes BÃ¡sicas</h6>
        <p><strong>Nome:</strong> {ativo.nome}</p>
        <p><strong>CÃ³digo:</strong> {ativo.codigo_patrimonio}</p>
        {/* Adicione mais campos conforme necessÃ¡rio */}
    </div>
);

const FormAtivo = ({ formData, onChange, onSubmit }) => (
    <form onSubmit={onSubmit}>
        <div className="row">
            <div className="col-md-6 mb-3">
                <label>Nome do Ativo</label>
                <input
                    type="text"
                    className="form-control"
                    name="nome"
                    value={formData.nome}
                    onChange={onChange}
                    required
                />
            </div>
            <div className="col-md-6 mb-3">
                <label>CÃ³digo de PatrimÃ´nio</label>
                <input
                    type="text"
                    className="form-control"
                    name="codigo_patrimonio"
                    value={formData.codigo_patrimonio}
                    onChange={onChange}
                    required
                />
            </div>
        </div>
        <button type="submit" className="btn btn-success">
            Salvar
        </button>
    </form>
);

const Modal = ({ id, title, children, onClose }) => (
    <div className="modal fade" id={id} tabIndex="-1" aria-hidden="true">
        <div className="modal-dialog modal-lg">
            <div className="modal-content">
                <div className="modal-header bg-info text-white">
                    <h5 className="modal-title">{title}</h5>
                    <button className="btn-close" onClick={onClose}></button>
                </div>
                <div className="modal-body">{children}</div>
            </div>
        </div>
    </div>
);

const FloatingButton = ({ icon, onClick }) => (
    <button
        className="btn btn-primary rounded-circle position-fixed"
        style={{
            bottom: "20px",
            right: "20px",
            width: "60px",
            height: "60px",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
        }}
        onClick={onClick}
    >
        <i className={icon}></i>
    </button>
);

export default AtivoFormModal;
