import React, { useEffect, useState, useCallback, useMemo } from "react";
import api from "../services/api";

const CATEGORIES = [
  { value: "todos", label: "Todas as Categorias" },
  { value: "Notebook", label: "Notebook" },
  { value: "Desktop", label: "Desktop" },
  { value: "Smartphone", label: "Smartphone" },
  { value: "Monitor", label: "Monitor" }
];

const AtivoList = () => {
  const [ativos, setAtivos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("todos");
  const [selectedAtivo, setSelectedAtivo] = useState(null);

  const loadAtivos = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get("ativos/");
      setAtivos(response.data);
    } catch (error) {
      console.error("Erro ao buscar ativos:", error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadAtivos();
  }, [loadAtivos]);

  const filteredAtivos = useMemo(() => {
    return ativos.filter((ativo) => {
      const searchFields = [
        ativo.nome, 
        ativo.codigo_patrimonio, 
        ativo.categoria, 
        ativo.responsavel
      ];

      const matchesSearch = searchFields.some(field => 
        field?.toLowerCase().includes(searchTerm.toLowerCase())
      );

      const matchesCategory = selectedCategory === "todos" || 
        ativo.categoria === selectedCategory;

      return matchesSearch && matchesCategory;
    });
  }, [searchTerm, selectedCategory, ativos]);

  const formatDate = (dateString) =>
    !dateString ? "-" : new Date(dateString).toLocaleDateString("pt-BR");

  return (
    <div className="container-fluid p-4">
      <div className="card border-0 shadow-lg rounded-4">
        <div className="card-header bg-primary text-white py-3 px-4 d-flex justify-content-between align-items-center">
          <h2 className="h4 mb-0 d-flex align-items-center">
            <i className="bi bi-list-task me-3"></i>
            Gerenciamento de Ativos
          </h2>
          <div className="d-flex gap-3">
            <div className="input-group input-group-sm shadow-sm">
              <span className="input-group-text bg-white border-end-0">
                <i className="bi bi-filter text-muted"></i>
              </span>
              <select
                className="form-select border-start-0"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {CATEGORIES.map(category => (
                  <option key={category.value} value={category.value}>
                    {category.label}
                  </option>
                ))}
              </select>
            </div>
            <div className="input-group input-group-sm shadow-sm">
              <span className="input-group-text bg-white border-end-0">
                <i className="bi bi-search text-muted"></i>
              </span>
              <input
                type="text"
                className="form-control border-start-0"
                placeholder="Pesquisar ativos..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
        </div>

        <div className="card-body p-0">
          {loading ? (
            <div className="d-flex justify-content-center align-items-center py-5">
              <div className="spinner-grow text-primary" role="status">
                <span className="visually-hidden">Carregando...</span>
              </div>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-striped table-hover mb-0">
                <thead className="bg-light">
                  <tr>
                    <th className="text-muted fw-normal">Nome do Ativo</th>
                    <th className="text-muted fw-normal">Patrimônio</th>
                    <th className="text-muted fw-normal">Categoria</th>
                    <th className="text-muted fw-normal">Responsável</th>
                    <th className="text-muted fw-normal">Setor</th>
                    <th className="text-muted fw-normal">Última Avaliação</th>
                    <th className="text-muted fw-normal">Próxima Avaliação</th>
                    <th className="text-muted fw-normal text-center">Ações</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredAtivos.length === 0 ? (
                    <tr>
                      <td colSpan="8" className="text-center py-4 text-muted">
                        <i className="bi bi-inbox fs-3 d-block mb-2"></i>
                        Nenhum ativo encontrado
                      </td>
                    </tr>
                  ) : (
                    filteredAtivos.map((ativo) => (
                      <tr key={ativo.id} className="align-middle">
                        <td>{ativo.nome}</td>
                        <td>{ativo.codigo_patrimonio}</td>
                        <td>
                          <span className={`badge bg-${
                            ativo.categoria === 'Notebook' ? 'primary' :
                            ativo.categoria === 'Desktop' ? 'success' :
                            ativo.categoria === 'Smartphone' ? 'warning' :
                            ativo.categoria === 'Monitor' ? 'info' : 'secondary'
                          }`}>
                            {ativo.categoria}
                          </span>
                        </td>
                        <td>{ativo.responsavel}</td>
                        <td>{ativo.setor}</td>
                        <td>{formatDate(ativo.data_ultima_avaliacao)}</td>
                        <td>{formatDate(ativo.data_proxima_avaliacao)}</td>
                        <td className="text-center">
                          <div className="btn-group btn-group-sm" role="group">
                            <button
                              className="btn btn-outline-info"
                              onClick={() => setSelectedAtivo(ativo)}
                              data-bs-toggle="modal"
                              data-bs-target="#visualizarModal"
                            >
                              <i className="bi bi-eye me-1"></i>Visualizar
                            </button>
                            <button className="btn btn-outline-warning">
                              <i className="bi bi-pencil me-1"></i>Editar
                            </button>
                            <button className="btn btn-outline-danger">
                              <i className="bi bi-trash me-1"></i>Excluir
                            </button>
                          </div>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Modal de Visualização */}
      <div
        className="modal fade"
        id="visualizarModal"
        tabIndex="-1"
        aria-labelledby="visualizarModalLabel"
        aria-hidden="true"
      >
        <div className="modal-dialog modal-lg modal-dialog-centered">
          <div className="modal-content border-0 shadow-lg">
            <div className="modal-header bg-light border-bottom-0">
              <h5 className="modal-title text-primary" id="visualizarModalLabel">
                <i className="bi bi-info-circle me-2"></i>Informações do Ativo
              </h5>
              <button
                type="button"
                className="btn-close"
                data-bs-dismiss="modal"
                aria-label="Close"
              ></button>
            </div>
            <div className="modal-body">
              {selectedAtivo ? (
                <div className="row g-3">
                  {Object.entries(selectedAtivo).map(([key, value]) => (
                    <div key={key} className="col-md-6">
                      <div className="bg-light rounded p-3 h-100">
                        <small className="text-muted text-uppercase d-block mb-1">
                          {key.replace(/_/g, " ")}
                        </small>
                        <p className="mb-0 fw-bold">
                          {value || "Não informado"}
                        </p>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p className="text-center text-muted">Nenhum ativo selecionado</p>
              )}
            </div>
            <div className="modal-footer bg-light border-top-0">
              <button
                type="button"
                className="btn btn-secondary"
                data-bs-dismiss="modal"
              >
                Fechar
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AtivoList;
