import React, { useState, useEffect } from "react";
import api from "../services/api";

const AtivoFormModal = () => {
    const [ativos, setAtivos] = useState([]);
    const [selectedAtivo, setSelectedAtivo] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [deletedAtivos, setDeletedAtivos] = useState([]);
    const [formData, setFormData] = useState({
        nome: "",
        codigo_patrimonio: "",
        categoria: "Notebook",
        localidade: "",
        setor: "",
        responsavel: "",
        status: "Ativo",
        data_ultima_avaliacao: "",
        data_proxima_avaliacao: "",
        modelo: "",
        marca: "",
        numero_serie: "",
        mac_address_ethernet: "",
        mac_address_wifi: "",
        ip_fixado: "",
        bitlocker: false,
        antivirus_licenca: "",
        antivirus_versao: "",
        bart_wazuh: false,
        chrome_enterprise: false,
        acesso_remoto_id: "",
        termo_custodia: false,
    });

    // Buscar ativos ao carregar o componente
    useEffect(() => {
        fetchAtivos();
    }, []);

    const fetchAtivos = async () => {
        try {
            const response = await api.get("ativos/");
            setAtivos(response.data);
        } catch (error) {
            console.error("Erro ao buscar ativos:", error);
        }
    };

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData({
            ...formData,
            [name]: type === "checkbox" ? checked : value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (isEditing) {
                await api.put(`ativos/${selectedAtivo.id}/`, formData);
                alert("Ativo atualizado com sucesso!");
            } else {
                await api.post("ativos/", formData);
                alert("Ativo adicionado com sucesso!");
            }
            resetForm();
            fetchAtivos();
        } catch (error) {
            alert(isEditing ? "Erro ao atualizar ativo." : "Erro ao adicionar ativo.");
        }
    };

    const handleDelete = async (ativo) => {
        if (window.confirm("Tem certeza que deseja excluir este ativo?")) {
            try {
                await api.delete(`ativos/${ativo.id}/`);
                setDeletedAtivos([...deletedAtivos, ativo]);
                fetchAtivos();
                alert("Ativo excluÃ­do com sucesso!");
            } catch (error) {
                alert("Erro ao excluir ativo.");
            }
        }
    };

    const handleUndo = async (ativo) => {
        try {
            await api.post("ativos/", ativo);
            setDeletedAtivos(deletedAtivos.filter(a => a.id !== ativo.id));
            fetchAtivos();
            alert("Ativo restaurado com sucesso!");
        } catch (error) {
            alert("Erro ao restaurar ativo.");
        }
    };

    const handleEdit = (ativo) => {
        setSelectedAtivo(ativo);
        setFormData(ativo);
        setIsEditing(true);
    };

    const resetForm = () => {
        setFormData({
            nome: "",
            codigo_patrimonio: "",
            categoria: "Notebook",
            localidade: "",
            setor: "",
            responsavel: "",
            status: "Ativo",
            data_ultima_avaliacao: "",
            data_proxima_avaliacao: "",
            modelo: "",
            marca: "",
            numero_serie: "",
            mac_address_ethernet: "",
            mac_address_wifi: "",
            ip_fixado: "",
            bitlocker: false,
            antivirus_licenca: "",
            antivirus_versao: "",
            bart_wazuh: false,
            chrome_enterprise: false,
            acesso_remoto_id: "",
            termo_custodia: false,
        });
        setIsEditing(false);
        setSelectedAtivo(null);
    };

    return (
        <div className="container-fluid">
            {/* Lista de Ativos */}
            <div className="table-responsive mt-4">
                <table className="table table-striped table-hover">
                    <thead className="table-dark">
                        <tr>
                            <th>Nome</th>
                            <th>CÃ³digo</th>
                            <th>Categoria</th>
                            <th>Setor</th>
                            <th>Status</th>
                            <th>AÃ§Ãµes</th>
                        </tr>
                    </thead>
                    <tbody>
                        {ativos.map((ativo) => (
                            <tr key={ativo.id}>
                                <td>{ativo.nome}</td>
                                <td>{ativo.codigo_patrimonio}</td>
                                <td>{ativo.categoria}</td>
                                <td>{ativo.setor}</td>
                                <td>
                                    <span className={`badge ${ativo.status === 'Ativo' ? 'bg-success' : 'bg-danger'}`}>
                                        {ativo.status}
                                    </span>
                                </td>
                                <td>
                                    <button
                                        className="btn btn-info btn-sm me-2"
                                        data-bs-toggle="modal"
                                        data-bs-target="#viewAtivoModal"
                                        onClick={() => setSelectedAtivo(ativo)}
                                    >
                                        Ver
                                    </button>
                                    <button
                                        className="btn btn-warning btn-sm me-2"
                                        data-bs-toggle="modal"
                                        data-bs-target="#ativoFormModal"
                                        onClick={() => handleEdit(ativo)}
                                    >
                                        Editar
                                    </button>
                                    <button
                                        className="btn btn-danger btn-sm"
                                        onClick={() => handleDelete(ativo)}
                                    >
                                        Excluir
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>

            {/* Modal de VisualizaÃ§Ã£o */}
            <div
                className="modal fade"
                id="viewAtivoModal"
                tabIndex="-1"
                aria-hidden="true"
            >
                <div className="modal-dialog modal-lg">
                    <div className="modal-content">
                        <div className="modal-header bg-info text-white">
                            <h5 className="modal-title">Detalhes do Ativo</h5>
                            <button
                                type="button"
                                className="btn-close"
                                data-bs-dismiss="modal"
                                aria-label="Close"
                            ></button>
                        </div>
                        <div className="modal-body">
                            {selectedAtivo && (
                                <div className="row g-3">
                                    <div className="col-md-6">
                                        <h6 className="text-muted">InformaÃ§Ãµes BÃ¡sicas</h6>
                                        <p><strong>Nome:</strong> {selectedAtivo.nome}</p>
                                        <p><strong>CÃ³digo:</strong> {selectedAtivo.codigo_patrimonio}</p>
                                        <p><strong>Categoria:</strong> {selectedAtivo.categoria}</p>
                                        <p><strong>Status:</strong> {selectedAtivo.status}</p>
                                    </div>
                                    <div className="col-md-6">
                                        <h6 className="text-muted">LocalizaÃ§Ã£o</h6>
                                        <p><strong>Localidade:</strong> {selectedAtivo.localidade}</p>
                                        <p><strong>Setor:</strong> {selectedAtivo.setor}</p>
                                        <p><strong>ResponsÃ¡vel:</strong> {selectedAtivo.responsavel}</p>
                                    </div>
                                    <div className="col-md-6">
                                        <h6 className="text-muted">EspecificaÃ§Ãµes</h6>
                                        <p><strong>Modelo:</strong> {selectedAtivo.modelo}</p>
                                        <p><strong>Marca:</strong> {selectedAtivo.marca}</p>
                                        <p><strong>NÃºmero de SÃ©rie:</strong> {selectedAtivo.numero_serie}</p>
                                    </div>
                                    <div className="col-md-6">
                                        <h6 className="text-muted">Rede</h6>
                                        <p><strong>MAC Ethernet:</strong> {selectedAtivo.mac_address_ethernet}</p>
                                        <p><strong>MAC WiFi:</strong> {selectedAtivo.mac_address_wifi}</p>
                                        <p><strong>IP Fixo:</strong> {selectedAtivo.ip_fixado}</p>
                                    </div>
                                    <div className="col-12">
                                        <h6 className="text-muted">SeguranÃ§a</h6>
                                        <div className="d-flex flex-wrap gap-3">
                                            <span className={`badge ${selectedAtivo.bitlocker ? 'bg-success' : 'bg-secondary'}`}>
                                                Bitlocker
                                            </span>
                                            <span className={`badge ${selectedAtivo.bart_wazuh ? 'bg-success' : 'bg-secondary'}`}>
                                                Bart/Wazuh
                                            </span>
                                            <span className={`badge ${selectedAtivo.chrome_enterprise ? 'bg-success' : 'bg-secondary'}`}>
                                                Chrome Enterprise
                                            </span>
                                            <span className={`badge ${selectedAtivo.termo_custodia ? 'bg-success' : 'bg-secondary'}`}>
                                                Termo de CustÃ³dia
                                            </span>
                                        </div>
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>

            {/* Modal de FormulÃ¡rio */}
            <div
                className="modal fade"
                id="ativoFormModal"
                tabIndex="-1"
                aria-hidden="true"
            >
                <div className="modal-dialog modal-lg">
                    <div className="modal-content">
                        <div className="modal-header" style={{ backgroundColor: "#451e44", color: "white" }}>
                            <h5 className="modal-title">
                                {isEditing ? "Editar Ativo" : "Adicionar Novo Ativo"}
                            </h5>
                            <button
                                type="button"
                                className="btn-close"
                                data-bs-dismiss="modal"
                                aria-label="Close"
                                style={{ filter: "invert(1)" }}
                            ></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={handleSubmit}>
                                {/* Linha 1 */}
                                <div className="row">
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">Nome do Ativo</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="nome"
                                            value={formData.nome}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>
                                    <div className="col-md-6 mb-3">
                                        <label className="form-label">CÃ³digo de PatrimÃ´nio</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="codigo_patrimonio"
                                            value={formData.codigo_patrimonio}
                                            onChange={handleChange}
                                            required
                                        />
                                    </div>
                                </div>

                                {/* Linha 2 */}
                                <div className="row">
                                    <div className="col-md-4 mb-3">
                                        <label className="form-label">Categoria</label>
                                        <select
                                            className="form-select"
                                            name="categoria"
                                            value={formData.categoria}
                                            onChange={handleChange}
                                        >
                                            <option value="Notebook">Notebook</option>
                                            <option value="Desktop">Desktop</option>
                                            <option value="Smartphone">Smartphone</option>
                                            <option value="Tablet">Tablet</option>
                                            <option value="Monitor">Monitor</option>
                                        </select>
                                    </div>
                                    <div className="col-md-4 mb-3">
                                        <label className="form-label">Localidade</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="localidade"
                                            value={formData.localidade}
                                            onChange={handleChange}
                                        />
                                    </div>
                                    <div className="col-md-4 mb-3">
                                        <label className="form-label">Setor</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            name="setor"
                                            value={formData.setor}
                                            onChange={handleChange}
                                        />
                                    </div>
                                </div>

                                {/* Resto do formulÃ¡rio permanece o mesmo */}
                                {/* ... (mantendo o restante do cÃ³digo do formulÃ¡rio) ... */}

                                {/* BotÃµes */}
                                <div className="d-flex justify-content-end mt-4">
                                    <button type="submit" className="btn btn-success me-2">
                                        {isEditing ? "Atualizar" : "Salvar"}
                                    </button>
                                    <button
                                        type="button"
                                        className="btn btn-secondary"
                                        data-bs-dismiss="modal"
                                        onClick={resetForm}
                                    >
                                        Cancelar
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>

            {/* BotÃ£o Flutuante para Adicionar */}
            <button
                type="button"
                className="btn btn-primary rounded-circle position-fixed"
                style={{
                    bottom: "20px",
                    right: "20px",
                    width: "60px",
                    height: "60px",
                    display: "flex",
                    justifyContent: "center",
                    alignItems: "center",
                    backgroundColor: "#451e44",
                    borderColor: "#000",
                    boxShadow: "0 2px 5px rgba(0,0,0,0.2)",
                }}
                data-bs-toggle="modal"
                data-bs-target="#ativoFormModal"
                onClick={resetForm}
            >                <i className="bi bi-plus" style={{ fontSize: "1.5rem", color: "white" }}></i>
            </button>
        </div>
    );
};

export default AtivoFormModal;
