import React, { useEffect, useState, useCallback, useMemo } from "react";
import api from "../services/api";

const CATEGORIES = [
  { value: "todos", label: "Todas as Categorias" },
  { value: "Notebook", label: "Notebook" },
  { value: "Desktop", label: "Desktop" },
  { value: "Smartphone", label: "Smartphone" },
  { value: "Monitor", label: "Monitor" }
];

const AtivoList = () => {
  const [ativos, setAtivos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("todos");
  const [selectedAtivo, setSelectedAtivo] = useState(null);
  const [editingAtivo, setEditingAtivo] = useState(null);

  // Carregar ativos
  const loadAtivos = useCallback(async () => {
    try {
      setLoading(true);
      const response = await api.get("ativos/");
      setAtivos(response.data);
    } catch (error) {
      console.error("Erro ao buscar ativos:", error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    loadAtivos();
  }, [loadAtivos]);

  // Filtrar ativos
  const filteredAtivos = useMemo(() => {
    return ativos.filter((ativo) => {
      const searchFields = [
        ativo.nome,
        ativo.codigo_patrimonio,
        ativo.categoria,
        ativo.responsavel
      ];

      const matchesSearch = searchFields.some((field) =>
        field?.toLowerCase().includes(searchTerm.toLowerCase())
      );

      const matchesCategory =
        selectedCategory === "todos" || ativo.categoria === selectedCategory;

      return matchesSearch && matchesCategory;
    });
  }, [searchTerm, selectedCategory, ativos]);

  // Formatar datas
  const formatDate = (dateString) =>
    !dateString ? "-" : new Date(dateString).toLocaleDateString("pt-BR");

  // Excluir ativo
  const handleDelete = async (ativoId) => {
    try {
      await api.delete(`ativos/${ativoId}/`);
      setAtivos(ativos.filter((ativo) => ativo.id !== ativoId));
      alert("Ativo excluído com sucesso!");
    } catch (error) {
      console.error("Erro ao excluir ativo:", error);
      alert("Erro ao excluir o ativo.");
    }
  };

  // Editar ativo
  const handleEdit = (ativo) => {
    setEditingAtivo(ativo);
  };

  const saveEdit = async () => {
    try {
      await api.put(`ativos/${editingAtivo.id}/`, editingAtivo);
      setAtivos(
        ativos.map((ativo) =>
          ativo.id === editingAtivo.id ? editingAtivo : ativo
        )
      );
      setEditingAtivo(null);
      alert("Ativo editado com sucesso!");
    } catch (error) {
      console.error("Erro ao editar ativo:", error);
      alert("Erro ao salvar as alterações.");
    }
  };

  return (
    <div className="container-fluid p-4">
      <div className="card border-0 shadow-lg rounded-4">
        <div className="card-header bg-primary text-white py-3 px-4 d-flex justify-content-between align-items-center">
          <h2 className="h4 mb-0 d-flex align-items-center">
            <i className="bi bi-list-task me-3"></i>
            Gerenciamento de Ativos
          </h2>
          <div className="d-flex gap-3">
            <div className="input-group input-group-sm shadow-sm">
              <span className="input-group-text bg-white border-end-0">
                <i className="bi bi-filter text-muted"></i>
              </span>
              <select
                className="form-select border-start-0"
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
              >
                {CATEGORIES.map((category) => (
                  <option key={category.value} value={category.value}>
                    {category.label}
                  </option>
                ))}
              </select>
            </div>
            <div className="input-group input-group-sm shadow-sm">
              <span className="input-group-text bg-white border-end-0">
                <i className="bi bi-search text-muted"></i>
              </span>
              <input
                type="text"
                className="form-control border-start-0"
                placeholder="Pesquisar ativos..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
        </div>

        <div className="card-body p-0">
          {loading ? (
            <div className="d-flex justify-content-center align-items-center py-5">
              <div className="spinner-grow text-primary" role="status">
                <span className="visually-hidden">Carregando...</span>
              </div>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-striped table-hover mb-0">
                <thead className="bg-light">
                  <tr>
                    <th>Nome do Ativo</th>
                    <th>Patrimônio</th>
                    <th>Categoria</th>
                    <th>Responsável</th>
                    <th>Setor</th>
                    <th>Última Avaliação</th>
                    <th>Próxima Avaliação</th>
                    <th className="text-center">Ações</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredAtivos.length === 0 ? (
                    <tr>
                      <td colSpan="8" className="text-center py-4 text-muted">
                        Nenhum ativo encontrado
                      </td>
                    </tr>
                  ) : (
                    filteredAtivos.map((ativo) => (
                      <tr key={ativo.id}>
                        <td>{ativo.nome}</td>
                        <td>{ativo.codigo_patrimonio}</td>
                        <td>{ativo.categoria}</td>
                        <td>{ativo.responsavel}</td>
                        <td>{ativo.setor}</td>
                        <td>{formatDate(ativo.data_ultima_avaliacao)}</td>
                        <td>{formatDate(ativo.data_proxima_avaliacao)}</td>
                        <td className="text-center">
                          <button
                            className="btn btn-sm btn-warning me-2"
                            onClick={() => handleEdit(ativo)}
                          >
                            Editar
                          </button>
                          <button
                            className="btn btn-sm btn-danger"
                            onClick={() => handleDelete(ativo.id)}
                          >
                            Excluir
                          </button>
                        </td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Modal de Edição */}
      {editingAtivo && (
        <div
          className="modal fade show d-block"
          tabIndex="-1"
          style={{ backgroundColor: "rgba(0, 0, 0, 0.5)" }}
        >
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Editar Ativo</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setEditingAtivo(null)}
                ></button>
              </div>
              <div className="modal-body">
                {Object.entries(editingAtivo).map(([key, value]) => (
                  <div className="mb-3" key={key}>
                    <label className="form-label">
                      {key.replace(/_/g, " ").toUpperCase()}
                    </label>
                    <input
                      type="text"
                      className="form-control"
                      value={value || ""}
                      onChange={(e) =>
                        setEditingAtivo({
                          ...editingAtivo,
                          [key]: e.target.value
                        })
                      }
                    />
                  </div>
                ))}
              </div>
              <div className="modal-footer">
                <button
                  className="btn btn-secondary"
                  onClick={() => setEditingAtivo(null)}
                >
                  Cancelar
                </button>
                <button className="btn btn-success" onClick={saveEdit}>
                  Salvar
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AtivoList;
